    },
    "referrer": `https://adventofcode.com/2020/leaderboard/private/view/${board}`,
    "method": "GET",
    "mode": "cors"
});

function replaceName( name) {

  let list = {
    'tomcss': 'Buck',
    'mspreij': 'Nomikos',
    'Bratteng': 'Vikingr'
  }

  if( list.hasOwnProperty( name)) {
    return list[name]
  } else {
    return name
  }
}

function idName( id) {
  const ids = {
    "756470": "aditsu"
  }

  return ids.hasOwnProperty( id) ? ids[id] : "anonymous users #"+id
}

let members = []

for(let key in data.members) {
  if( data.members[key].name === null) {
    data.members[key].name = idName( data.members[key].id)
  }
  if( data.members[key].stars>0) members.push( data.members[key])
}

members.sort( (a, b) => (b.stars*1000+b.local_score)-(a.stars*1000+a.local_score))

let ranking = members.map( (member, i) => `{b}${i+1}{/}: {g}${ replaceName(member.name)}{/} {b}({/}{y}${member.stars}{/}{b}){/}`).join(', ')

print( `{dg}AoC{/} ranking {g}2020{/}: ${ranking}`)
