(async () => {
    const alex = await acquire('alex'); // 6.0.0 works
    const [user, quantity] = IRC.command.params;
    const { from } = IRC.message;
    const message = IRC.log.user(user || from, '', quantity, 0).map(d => d.message).join` `;
    const errors = alex(message).messages
    const errorList = errors.map(d => d.message).join`, `.toLowerCase();
    if (errors.length) {
        print.error(Error((user||from) + ': ' + errorList));
    } else {
        print.success('seems ok');
    }
})();
