command:
let competitor = input
let comp_name = IRC.command.params[0];
// Get closest matching competition to query
let obj = fetchSync("http://fca.ickler.cloud/api/v0/competitions?q="+comp_name+"&start="+new Date().toISOString().split("T")[0],{"type": "json"})

if (obj){
    let comp = obj[0]
    if(!comp){
	    print("Comp in the past");
	    return;
    }
    let comp_id = comp["url"].split("/")[4]
    let url = "http://fca.ickler.cloud/api/v0/competitions/"+comp_id+"/wcif"
    let wcif = fetchSync(url,{type:"json"})
    let competitors = wcif["persons"]
    let search = competitors.filter( x => x["name"].toLowerCase().indexOf(competitor.toLowerCase()) != -1)

    if(search.length == 1){
        print(search[0]["name"] + " is coming to " + comp["name"]) 
    }
    else if (search.length == 0){
        print("There is no " + competitor + " coming to " + comp["name"])
    }	else{
	print("Competitors matching the query")
        search.map((x,i) => print((i + 1) + ". " +  x["name"]))
    }

}else{
    print("No comp found")
}
