if (input == "UK") {
    input = "United Kingdom";
}

let obj = fetchSync("https://worldcubeassociation.org/api/v0/competitions?q="+input+"&start="+new Date().toISOString().split("T")[0],{"type": "json"})
if (obj && obj.length > 0){
    let resultsString = ""
    for(let i = 1; i <= obj.length; i++){
        resultsString = `${obj[obj.length - i]["short_name"]}`;
        resultsString += ` `.repeat(35-resultsString.length);

        if (obj.length - i != 0) {
            resultsString += `|| ${obj[obj.length - i - 1]["short_name"]}`;
        }

        if (i % 2 != 0) {
            resultsString += "\n";
        }

        i++;
        print(resultsString)
    }
}
else {
    print("No Comps found")
}
