const date = new Date().toISOString();
// Check for comps up to 5 Days ago (that are new)
const dateMinus5 = new Date()
dateMinus5.setDate(dateMinus5.getDate() - 5)
const data = fetchSync("https://www.worldcubeassociation.org/api/v0/competitions?q=&announced_after="+dateMinus5.toISOString(),{"type":"json"})

if (data){
    const comps = data.filter( comp => new Date(comp["announced_at"]) > new Date(store.get("last")))
    if(comps.length != 0){
        print(`Announcements for ${date}:`)
        for(let i = 0; i < comps.length; i++){
            const comp = comps[i]
            print(`Announced {y}${comp.name}{/} in {o}${comp.city}, ${comp.country_iso2}{/}. {g}${comp.start_date} to ${comp.end_date}{/}.`)
        }
    }else{
        print("No newly announced Comps since last run :(")
    }
}else{
    print("Can't connect to the WCA API")
}
store.set("last",date)
