// Get closest matching competition to query
let obj = fetchSync("https://www.worldcubeassociation.org/api/v0/competitions?q="+input+"&start="+new Date().toISOString().split("T")[0],{"type": "json"})

if (obj){
    let comp = obj[0]
    if(!comp){
        print("Competition not announced");
        return
    }
    print(comp["name"])
    let comp_id = comp["url"].split("/")[4]
    let dom = fetchSync("https://www.worldcubeassociation.org/competitions/"+comp_id,{type:"dom"})

    let dates = dom.document.getElementsByClassName("wca-local-time")
    let days = (new Date(dates[0].attributes["data-utc-time"].nodeValue) - new Date()) / (1000*60*60*24)

    if(days < 0){
        print("Registration is open")
    }
    else if (days < 1){
        print(`Registration will open in ${Math.floor(days * 24)} hours`)
    }
    else {
        print(`Registration will open in ${Math.floor(days)} days`)
    }


}else{
    print("No comp found")
}
