const items = [
    ...fetchSync
        .dom('https://www.cosarara.me/blog/atom.xml')
        .document.body.querySelectorAll('entry'),
].map(n =>({
    title: n.querySelector('title').textContent,
    desc: _.truncate(n.querySelector('content').textContent, { length: 150 }).replace(/<.*?>/g, ''),
    url: n.querySelector('id').textContent,
}));

items.reverse().slice(0, 1)
    .forEach(({ title, desc, url }) => {
        print(`{g}${title}{/} ${IRC.colors.link(url)} ${desc}`)
    });
