const centers = [
    // U B R F L D
    { color: 0, axis: 'y', offset: -1 },
    { color: 1, axis: 'z', offset: -1 },
    { color: 2, axis: 'x', offset: 1 },
    { color: 3, axis: 'z', offset: 1 },
    { color: 4, axis: 'x', offset: -1 },
    { color: 5, axis: 'y', offset: 1 },
];

const edges = [
    // UB UR UF UL
    [ { color: 1, axis: 'z', offset: -1 }, { color: 0, axis: 'y', offset: -1 } ],
    [ { color: 2, axis: 'x', offset: 1 }, { color: 0, axis: 'y', offset: -1 } ],
    [ { color: 3, axis: 'z', offset: 1 }, { color: 0, axis: 'y', offset: -1 } ],
    [ { color: 4, axis: 'x', offset: -1 }, { color: 0, axis: 'y', offset: -1 } ],
    // BR FR FL BL
    [ { color: 2, axis: 'x', offset: 1 }, { color: 1, axis: 'z', offset: -1 } ],
    [ { color: 2, axis: 'x', offset: 1 }, { color: 3, axis: 'z', offset: 1 } ],
    [ { color: 4, axis: 'x', offset: -1 }, { color: 3, axis: 'z', offset: 1 } ],
    [ { color: 4, axis: 'x', offset: -1 }, { color: 1, axis: 'z', offset: -1 } ],
    // DB DR DF DL
    [ { color: 1, axis: 'z', offset: -1 }, { color: 5, axis: 'y', offset: 1 } ],
    [ { color: 2, axis: 'x', offset: 1 }, { color: 5, axis: 'y', offset: 1 } ],
    [ { color: 3, axis: 'z', offset: 1 }, { color: 5, axis: 'y', offset: 1 } ],
    [ { color: 4, axis: 'x', offset: -1 }, { color: 5, axis: 'y', offset: 1 } ],
];

const corners = [
    // UBR URF UFL ULB
    [
        { color: 2, axis: 'x', offset: 1 },
        { color: 0, axis: 'y', offset: -1 },
        { color: 1, axis: 'z', offset: -1 },
    ],
    [
        { color: 2, axis: 'x', offset: 1 },
        { color: 0, axis: 'y', offset: -1 },
        { color: 3, axis: 'z', offset: 1 },
    ],
    [
        { color: 4, axis: 'x', offset: -1 },
        { color: 0, axis: 'y', offset: -1 },
        { color: 3, axis: 'z', offset: 1 },
    ],
    [
        { color: 4, axis: 'x', offset: -1 },
        { color: 0, axis: 'y', offset: -1 },
        { color: 1, axis: 'z', offset: -1 },
    ],
    // DRB DFR DLF DBL
    [
        { color: 2, axis: 'x', offset: 1 },
        { color: 5, axis: 'y', offset: 1 },
        { color: 1, axis: 'z', offset: -1 },
    ],
    [
        { color: 2, axis: 'x', offset: 1 },
        { color: 5, axis: 'y', offset: 1 },
        { color: 3, axis: 'z', offset: 1 },
    ],
    [
        { color: 4, axis: 'x', offset: -1 },
        { color: 5, axis: 'y', offset: 1 },
        { color: 3, axis: 'z', offset: 1 },
    ],
    [
        { color: 4, axis: 'x', offset: -1 },
        { color: 5, axis: 'y', offset: 1 },
        { color: 1, axis: 'z', offset: -1 },
    ],
];

const cube = {
    edges,
    corners,
    centers,
};

// U B R F L D
const moveList = {
    R: {
        edges: [5, 9, 4, 1],
        corners: [5, 4, 0, 1],
        centre: 2,
        axis: 'x',
    },
    U: {
        edges: [3, 2, 1, 0],
        corners: [3, 2, 1, 0],
        centre: 0,
        axis: 'y',
    },
    F: {
        edges: [6, 10, 5, 2],
        corners: [1, 2, 6, 5],
        centre: 3,
        axis: 'z',
    },
    L: {
        edges: [3, 7, 11, 6],
        corners: [2, 3, 7, 6],
        centre: 4,
        axis: 'x',
        axisFlip: true,
    },
    B: {
        edges: [4, 8, 7, 0],
        corners: [4, 7, 3, 0],
        centre: 1,
        axis: 'z',
        axisFlip: true,
    },
    D: {
        edges: [8, 9, 10, 11],
        corners: [4, 5, 6, 7],
        centre: 5,
        axis: 'y',
        axisFlip: true,
    },
    M: {
        centers: [0, 1, 5, 3],
        edges: [2, 0, 8, 10],
        axis: 'x',
        axisFlip: true,
    },
    E: {
        centers: [3, 4, 1, 2],
        edges: [4, 5, 6, 7],
        axis: 'y',
        axisFlip: true,
    },
    S: {
        centers: [4, 5, 2, 0],
        edges: [3, 11, 9, 1],
        axis: 'z',
    },
    r: { moves: [toObject(`R`), toObject(`M'`)] },
    l: { moves: [toObject(`L`), toObject(`M`)] },
    f: { moves: [toObject(`F`), toObject(`S`)] },
    b: { moves: [toObject(`B`), toObject(`S'`)] },
    u: { moves: [toObject(`U`), toObject(`E'`)] },
    d: { moves: [toObject(`D`), toObject(`E`)] },
    x: { moves: [toObject(`R`), toObject(`M'`), toObject(`L'`)] },
    y: { moves: [toObject(`U`), toObject(`E'`), toObject(`D'`)] },
    z: { moves: [toObject(`F`), toObject(`S`), toObject(`B'`)] },
};

function getMove(moveRaw) {
    const { move, order } = toObject(moveRaw);
    if (!moveList[move]) throw new Error(`invalid move ${move}`);
    const {
        corners,
        edges,
        centers,
        centre,
        axis,
        axisFlip,
        moves,
    } = moveList[move];

    // calculate transforms
    const transforms = [];
    corners && transforms.push(...corners.map(index => cube.corners[index]));
    edges && transforms.push(...edges.map(index => cube.edges[index]));
    centers && transforms.push(...centers.map(index => cube.centers[index]));
    typeof centre !== 'undefined' && transforms.push(cube.centers[centre]);
    const axisOrder = axisFlip ? -1 : 1;
    const extraMoves = moves && (
        moves.map(move => getMove(applyOrder(clone(move), order), cube))
    );

    // swap stickers / clean up move
    function apply() {
        extraMoves && extraMoves.forEach(move => move.apply());
        if (edges) {
            doCycle(cube.edges, order, edges, centers ? 'z' : axis);
        }
        if (centers) {
            doCycle(cube.centers, order, centers);
        }
        if (corners) {
            doCycle(cube.corners, order, corners, axis);
        }
    }

    return {
        apply,
        transforms,
        axis,
        source: { move, order },
    };
}

function getMoves(moves) {
    return splitMoves(moves).map(move => getMove(move, cube));
}

function splitMoves(str) {
    if (typeof str !== 'string') return str;
    return str.split(/(\ww?\d?'?)/).filter(d => d.trim());
}

function toObject(move) {
    if (typeof move !== 'string') return move;
    if (move[1] === 'w') move = `${move[0].toLowerCase()}${move.slice(2) || ''}`;
    return {
        move: move[0],
        order: {
            '\'': -1,
            '2\'': -2,
            '2': 2,
        }[move.slice(1)] || 1,
    };
}

function clone(move) {
    return Object.assign({}, move);
}

function applyOrder(move, order) {
    if (order === 1) return move;
    if (order === -1) {
        move.order = move.order === 2 ? 2 : -move.order;
    }
    if (order === 2) {
        move.order = move.order === 2 ? 0 : 2;
    }
    return move;
}


const cornerSwaps = {
    x: [1, 2],
    y: [0, 2],
    z: [0, 1],
};

function doCycle(arr, order, cycle, axis) {
    if (order === 0) return;
    if (order === -1 || order === 3) {
        cycle = [...cycle].reverse();
    }
    if (order === 2 || order === -2) {
        doCycle(arr, 1, cycle, axis);
    }

    // cycles
    for (let i = 0; i < cycle.length - 1; i++) {
        swapPiece(arr, cycle[i], cycle[i + 1]);
    }

    // corner 'twists'
    if (arr[0].length === 3) {
        for (let i = 0; i < cycle.length; i++) {
            swapSticker(arr[cycle[i]], ...cornerSwaps[axis]);
        }
    }
    // edge flips
    else if (axis === 'z') {
        for (let i = 0; i < cycle.length; i++) {
            swapSticker(arr[cycle[i]], 0, 1);
        }
    }
}

function swapPiece(arr, first, second) {
    if (Array.isArray(arr[first])) {
        const tmpColors = arr[first].map(d => d.color);
        arr[first].forEach((sticker, i) => { sticker.color = arr[second][i].color; });
        arr[second].forEach((sticker, i) => { sticker.color = tmpColors[i]; });
    } else {
        swapSticker(arr, first, second)
    }
}

function swapSticker(arr, first, second) {
    const tmp = arr[first].color;
    arr[first].color = arr[second].color;
    arr[second].color = tmp;
}

function isSolved() {
    const { centers, edges, corners } = cube;
    const lookup = {};
    centers.forEach(({ color, axis, offset }) => {
        lookup[`${axis}|${offset}`] = color;
    });
    const pieces = edges.concat(corners);
    for (let i = 0; i < pieces.length; i++) {
        for (let j = 0; j < pieces[i].length; j++) {
            const { axis, offset, color } = pieces[i][j];
            if (color !== lookup[`${axis}|${offset}`]) {
                return false;
            }
        }
    }
    return true;
}


// IRC stuff

const { from } = IRC.message;

function saveCube() {
    store.set(from, JSON.stringify(cube));
}

function loadCube() {
    const next = store.get(from);
    if (next) {
        Object.assign(cube, JSON.parse(next));
    }
}

function doMoves(moves) {
    getMoves(moves).forEach(move => move.apply());
}

// render

const colors = 'wbrgoy';

function renderQCube(message = '') {
    function sticker(obj) {
        return `{${colors[obj.color]}}██{/}`;
    }

    print([
        [sticker(cube.corners[3][0]),sticker(cube.corners[3][1]),sticker(cube.edges[0][1]),sticker(cube.corners[0][1]),sticker(cube.corners[0][0])].join`` + ` ${message}`,
        [sticker(cube.edges[3][0]),sticker(cube.edges[3][1]),sticker(cube.centers[0]),sticker(cube.edges[1][1]),sticker(cube.edges[1][0])].join``,
        [sticker(cube.corners[2][0]),sticker(cube.corners[2][1]),sticker(cube.edges[2][1]),sticker(cube.corners[1][1]),sticker(cube.corners[1][0])].join``,
        [sticker(cube.corners[2][0]),sticker(cube.corners[2][2]),sticker(cube.edges[2][0]),sticker(cube.corners[1][2]),sticker(cube.corners[1][0])].join``,
        [sticker(cube.edges[6][0]),sticker(cube.edges[6][1]),sticker(cube.centers[3]),sticker(cube.edges[5][1]),sticker(cube.edges[5][0])].join``,
        [sticker(cube.corners[6][0]),sticker(cube.corners[6][2]),sticker(cube.edges[0xA][0]),sticker(cube.corners[5][2]),sticker(cube.corners[5][0])].join``,
    ].join`\n`)
}

function renderMini(message = '') {
    function sticker(obj) {
        return colors[obj.color]; 
    }
    function crunch([l1, l2]) {
        return l1.map((d, i) => `{${d},${l2[i]}}▀{/}`).join``;
    }
    print([
        crunch([[sticker(cube.corners[3][0]),sticker(cube.corners[3][1]),sticker(cube.edges[0][1]),sticker(cube.corners[0][1]),sticker(cube.corners[0][0])],
        [sticker(cube.edges[3][0]),sticker(cube.edges[3][1]),sticker(cube.centers[0]),sticker(cube.edges[1][1]),sticker(cube.edges[1][0])]]) + ` ${message}`,
        crunch([[sticker(cube.corners[2][0]),sticker(cube.corners[2][1]),sticker(cube.edges[2][1]),sticker(cube.corners[1][1]),sticker(cube.corners[1][0])],
        [sticker(cube.corners[2][0]),sticker(cube.corners[2][2]),sticker(cube.edges[2][0]),sticker(cube.corners[1][2]),sticker(cube.corners[1][0])]]),
        crunch([[sticker(cube.edges[6][0]),sticker(cube.edges[6][1]),sticker(cube.centers[3]),sticker(cube.edges[5][1]),sticker(cube.edges[5][0])],
        [sticker(cube.corners[6][0]),sticker(cube.corners[6][2]),sticker(cube.edges[0xA][0]),sticker(cube.corners[5][2]),sticker(cube.corners[5][0])]]),
    ].join`\n`)
}

function renderT(message = '') {
    function sticker(obj) { return `{${colors[obj.color]}}██{/}`; }
    function crnr(x, y) { return sticker(cube.corners[x][y]); }
    function edge(x, y) { return sticker(cube.edges[x][y]); }
    function cntr(x) { return sticker(cube.centers[x]); }
    const spaces = "      ";
    print([
        [spaces,crnr(3,1),edge(0,1),crnr(0,1)].join`` + ` ${message}`,
        [spaces,edge(3,1),cntr(0),  edge(1,1)].join``,
        [spaces,crnr(2,1),edge(2,1),crnr(1,1)].join``,
        [crnr(3,0),edge(3,0),crnr(2,0),crnr(2,2),edge(2,0),crnr(1,2),crnr(1,0),edge(1,0),crnr(0,0)].join``,
        [edge(7,0),cntr(4),edge(6,0),edge(6,1),cntr(3),  edge(5,1),edge(5,0),cntr(2),edge(4,0)].join``,
        [crnr(7,0),edge(11,0),crnr(6,0),crnr(6,2),edge(10,0),crnr(5,2),crnr(5,0),edge(9,0),crnr(4,0)].join``,
    ].join`\n`)
}


function mini() {
    doMoves(input);
    renderMini();
}

function view(view) {
    if (!view) {
        throw `usage: ${IRC.colors.cmd('cube.view', 'qcube|mini|T')}`;
    }
    store.set(`${from}:view`, view);
    print(`View for ${from} set to ${view}`);
}

function getView() { return  store.get(`${from}:view`) || 'qcube'; }

function render(message) {
    const view = getView();
    if (view === 'mini') return renderMini(message);
    else if (view === 'T') return renderT(message);
    return renderQCube();
}

// solving / scramble / timer

const scrambler = IRC.require('scramble');

function scramble(type) {
    type = type.toUpperCase().trim();
    !['LL', 'LSE', 'TEST'].includes(type) && (type = '3x3');

    const scramble = {
        TEST: `RU`,
        LL: scrambler.getLLScramble(),
        LSE: `xy'` + scrambler.getLSEScramble() + ['', ' M2'][0|Math.random()*2],
    }[type] || (() => scrambler.getRandomScramble())();

    doMoves(scramble);

    store.save(`${from}:solve`, {
        moves: [],
        scrambleTime: +(new Date),
        startTime: undefined,
        endTime: undefined,
        scramble,
        view: getView(),
        type,
    });

    saveCube();
    render(`${type} scramble`);
}

function apply(inputMoves) {
    loadCube();
    doMoves(inputMoves);
    saveCube();

    const state = store.load(`${from}:solve`, false);
    let message;
    if (state) {
        let { moves, startTime, ...other } = state;

        moves.push([+(new Date), inputMoves]);

        if (!startTime) {
            if (getMoves(inputMoves).some(move => !'xyz'.includes(move.source.move))) {
                // if there's a non inspection move
                startTime = +(new Date);
                message = 'timer started!';
            }
        }

        store.save(`${from}:solve`, {
            moves,
            startTime,
            ...other
        });


        if (isSolved()) return solved();
    }

    render(message);
}

function renderDiff(startTime, endTime) {
    const { intervalToDuration } = require('date-fns@2.28.0');
    const duration = intervalToDuration({ start: new Date(startTime), end: new Date(endTime) });
    return `${duration.minutes}:${duration.seconds}`;
}

function solved() {
    const { moves, startTime, scrambleTime, scramble, view, type } = store.load(`${from}:solve`);
    const endTime = +(new Date);
    const score = endTime - startTime;
    render(`solved in ${renderDiff(startTime, endTime)}!`);

    const scores = store.load(`scores:${type}`, {});
    const oldScore = scores[from] || Infinity;

    if (score > oldScore) {
        scores[from] = score;
        store.save(`scores:${type}`, scores);
        store.save(`${from}:solution`, {
            startTime, endTime, score, scramble, moves, view, type
        });
        print(`{g}new pb!{/}`);
    }

    store.save(`${from}:solve`, false);
}



