function getMove(moveRaw) {
    const { move, order } = toObject(moveRaw);
    if (!moveList[move]) throw new Error(`invalid move ${move}`);
    const {
        corners,
        edges,
        centres,
        centre,
        axis,
        axisFlip,
        moves,
    } = moveList[move];

    // calculate transforms
    const transforms = [];
    corners && transforms.push(...corners.map(index => cube.corners[index]));
    edges && transforms.push(...edges.map(index => cube.edges[index]));
    centres && transforms.push(...centres.map(index => cube.centres[index]));
    typeof centre !== 'undefined' && transforms.push(cube.centres[centre]);
    const axisOrder = axisFlip ? -1 : 1;
    const extraMoves = moves && (
        moves.map(move => getMove(applyOrder(clone(move), order), cube))
    );

    // swap stickers / clean up move
    function apply() {
        extraMoves && extraMoves.forEach(move => move.apply());
        if (edges) {
            doCycle(cube.edges, order, edges, centres ? 'z' : axis);
        }
        if (centres) {
            doCycle(cube.centres, order, centres);
        }
        if (corners) {
            doCycle(cube.corners, order, corners, axis);
        }
    }

    return {
        apply,
        transforms,
        axis,
        source: { move, order },
    };
}

function getMoves(moves) {
    return splitMoves(moves).map(move => getMove(move, cube));
}

function splitMoves(str) {
    if (typeof str !== 'string') return str;
    return str.split(/(\ww?\d?'?)/).filter(d => d.trim());
}

function toObject(move) {
    if (typeof move !== 'string') return move;
    if (move[1] === 'w') move = `${move[0].toLowerCase()}${move.slice(2) || ''}`;
    return {
        move: move[0],
        order: {
            '\'': -1,
            '2\'': -2,
            '2': 2,
        }[move.slice(1)] || 1,
    };
}

function clone(move) {
    return Object.assign({}, move);
}

function applyOrder(move, order) {
    if (order === 1) return move;
    if (order === -1) {
        move.order = move.order === 2 ? 2 : -move.order;
    }
    if (order === 2) {
        move.order = move.order === 2 ? 0 : 2;
    }
    return move;
}


const cornerSwaps = {
    x: [1, 2],
    y: [0, 2],
    z: [0, 1],
};

function doCycle(arr, order, cycle, axis) {
    if (order === 0) return;
    if (order === -1 || order === 3) {
        cycle = [...cycle].reverse();
    }
    if (order === 2 || order === -2) {
        doCycle(arr, 1, cycle, axis);
    }

    // cycles
    for (let i = 0; i < cycle.length - 1; i++) {
        swap(arr, cycle[i], cycle[i + 1]);
    }

    // corner 'twists'
    if (arr[0].length === 3) {
        for (let i = 0; i < cycle.length; i++) {
            swap(arr[cycle[i]], ...cornerSwaps[axis]);
        }
    }
    // edge flips
    else if (axis === 'z') {
        for (let i = 0; i < cycle.length; i++) {
            swap(arr[cycle[i]], 0, 1);
        }
    }
}

function swap(arr, first, second) {
    const tmp = arr[first];
    arr[first] = arr[second];
    arr[second] = tmp;
}


