        { color: 1, axis: 'z', offset: -1 },
    ],
];

const cube = {
    edges,
    corners,
    centres,
};

// U B R F L D
const moveList = {
    R: {
        edges: [5, 9, 4, 1],
        corners: [5, 4, 0, 1],
        centre: 2,
        axis: 'x',
    },
    U: {
        edges: [3, 2, 1, 0],
        corners: [3, 2, 1, 0],
        centre: 0,
        axis: 'y',
    },
    F: {
        edges: [6, 10, 5, 2],
        corners: [1, 2, 6, 5],
        centre: 3,
        axis: 'z',
    },
    L: {
        edges: [3, 7, 11, 6],
        corners: [2, 3, 7, 6],
        centre: 4,
        axis: 'x',
        axisFlip: true,
    },
    B: {
        edges: [4, 8, 7, 0],
        corners: [4, 7, 3, 0],
        centre: 1,
        axis: 'z',
        axisFlip: true,
    },
    D: {
        edges: [8, 9, 10, 11],
        corners: [4, 5, 6, 7],
        centre: 5,
        axis: 'y',
        axisFlip: true,
    },
    M: {
        centres: [0, 1, 5, 3],
        edges: [2, 0, 8, 10],
        axis: 'x',
        axisFlip: true,
    },
    E: {
        centres: [3, 4, 1, 2],
        edges: [4, 5, 6, 7],
        axis: 'y',
        axisFlip: true,
    },
    S: {
        centres: [4, 5, 2, 0],
        edges: [3, 11, 9, 1],
        axis: 'z',
    },
    r: { moves: [toObject(`R`), toObject(`M'`)] },
    l: { moves: [toObject(`L`), toObject(`M`)] },
    f: { moves: [toObject(`F`), toObject(`S`)] },
    b: { moves: [toObject(`B`), toObject(`S'`)] },
    u: { moves: [toObject(`U`), toObject(`E'`)] },
    d: { moves: [toObject(`D`), toObject(`E`)] },
    x: { moves: [toObject(`R`), toObject(`M'`), toObject(`L'`)] },
    y: { moves: [toObject(`U`), toObject(`E'`), toObject(`D'`)] },
    z: { moves: [toObject(`F`), toObject(`S`), toObject(`B'`)] },
};

function getMove(moveRaw) {
    const { move, order } = toObject(moveRaw);
    if (!moveList[move]) throw new Error(`invalid move ${move}`);
    const {
        corners,
        edges,
        centres,
        centre,
        axis,
        axisFlip,
        moves,
    } = moveList[move];

    // calculate transforms
    const transforms = [];
    corners && transforms.push(...corners.map(index => cube.corners[index]));
    edges && transforms.push(...edges.map(index => cube.edges[index]));
    centres && transforms.push(...centres.map(index => cube.centres[index]));
    typeof centre !== 'undefined' && transforms.push(cube.centres[centre]);
    const axisOrder = axisFlip ? -1 : 1;
    const extraMoves = moves && (
        moves.map(move => getMove(applyOrder(clone(move), order), cube))
    );

    // swap stickers / clean up move
    function apply() {
        extraMoves && extraMoves.forEach(move => move.apply());
        if (edges) {
            doCycle(cube.edges, order, edges, centres ? 'z' : axis);
        }
        if (centres) {
            doCycle(cube.centres, order, centres);
        }
        if (corners) {
            doCycle(cube.corners, order, corners, axis);
        }
    }

    return {
        apply,
        transforms,
        axis,
        source: { move, order },
    };
}

function getMoves(moves) {
    return splitMoves(moves).map(move => getMove(move, cube));
}

function splitMoves(str) {
    if (typeof str !== 'string') return str;
    return str.split(/(\ww?\d?'?)/).filter(d => d.trim());
}

function toObject(move) {
    if (typeof move !== 'string') return move;
    if (move[1] === 'w') move = `${move[0].toLowerCase()}${move.slice(2) || ''}`;
    return {
        move: move[0],
        order: {
            '\'': -1,
            '2\'': -2,
            '2': 2,
        }[move.slice(1)] || 1,
    };
}

function clone(move) {
    return Object.assign({}, move);
}

function applyOrder(move, order) {
    if (order === 1) return move;
    if (order === -1) {
        move.order = move.order === 2 ? 2 : -move.order;
    }
    if (order === 2) {
        move.order = move.order === 2 ? 0 : 2;
    }
    return move;
}


const cornerSwaps = {
    x: [1, 2],
    y: [0, 2],
    z: [0, 1],
};

function doCycle(arr, order, cycle, axis) {
    if (order === 0) return;
    if (order === -1 || order === 3) {
        cycle = [...cycle].reverse();
    }
    if (order === 2 || order === -2) {
        doCycle(arr, 1, cycle, axis);
    }

    // cycles
    for (let i = 0; i < cycle.length - 1; i++) {
        swapPiece(arr, cycle[i], cycle[i + 1]);
    }

    // corner 'twists'
    if (arr[0].length === 3) {
        for (let i = 0; i < cycle.length; i++) {
            swapSticker(arr[cycle[i]], ...cornerSwaps[axis]);
        }
    }
    // edge flips
    else if (axis === 'z') {
        for (let i = 0; i < cycle.length; i++) {
            swapSticker(arr[cycle[i]], 0, 1);
        }
    }
}

function swapPiece(arr, first, second) {
    if (Array.isArray(arr[first])) {
        const tmpColors = arr[first].map(d => d.color);
        arr[first].forEach((sticker, i) => { sticker.color = arr[second][i].color; });
        arr[second].forEach((sticker, i) => { sticker.color = tmpColors[i]; });
    } else {
        swapSticker(arr, first, second)
    }
}

function swapSticker(arr, first, second) {
    const tmp = arr[first].color;
    arr[first].color = arr[second].color;
    arr[second].color = tmp;
}

function isSolved() {
    const { centres, edges, corners } = cube;
    const lookup = {};
    centres.forEach(({ color, axis, offset }) => {
        lookup[`${axis}|${offset}`] = color;
    });
    const pieces = edges.concat(corners);
    for (let i = 0; i < pieces.length; i++) {
        for (let j = 0; j < pieces[i].length; j++) {
            const { axis, offset, color } = pieces[i][j];
            if (color !== lookup[`${axis}|${offset}`]) {
                return false;
            }
        }
    }
    return true;
}


// IRC stuff

const { from } = IRC.message;

function saveCube() {
    store.set(from, JSON.stringify(cube));
}

function loadCube() {
    const next = store.get(from);
    if (next) {
        Object.assign(cube, JSON.parse(next));
    }
}

function doMoves(moves) {
    getMoves(moves).forEach(move => move.apply());
}

// render

const colors = 'wbrgoy';

function renderNormal(message = '') {
    function sticker(obj) {
        return colors[obj.color];
    }

    function crnr(x, y) {
      return sticker(cube.corners[x][y]);
    }

  	function edge(x, y) {
      return sticker(cube.edges[x][y]);
    }

    function cntr(x) {
      return sticker(cube.centres[x]);
    }

    function two(x, y) {
      return `{${x},${y}}▀{/}`;
    }

    function full(x) {
      return two(x, x);
    }

    function down(x) {
      return `{${x}}▄{/}`;
    }

    function up(x) {
      return `{${x}}▀{/}`;
    }

    function hex(x,i) {
      return parseInt(x.charAt(i),16);
    }

    function parse(x) {
      switch (x.charAt(0)) {
        case 'c': return crnr(hex(x,1), hex(x,2));
        case 'e': return edge(hex(x,1), hex(x,2));
        case 't': return cntr(hex(x,1));
      }
    }     

    function cell(s) {
      var a = s.split(",");
      switch(a[0]) {
        case 'u': return up(parse(a[1]));
        case 'd': return down(parse(a[1]));
        case '2': return two(parse(a[1]), parse(a[2]));
        case 'f': return full(parse(a[1]));
      }
    }

    function line(s) {
      return s.split(';').map(cell).join``;
    }

    print([
        "    " + line("d,c31;f,c31;f,c31;d,e01;d,e01") + ` ${message}`,
        "  " + line("d,e31;f,e31;f,e31;2,c31,t0;2,e01,t0;f,e01;2,e01,c01;f,c01;f,c01;d,c00"),
        line("d,c21;f,c21;f,c21;2,e31,e21;2,t0,e21;f,t0;2,t0,e11;f,e11;f,e11;2,c01,e10;f,c00;f,c00"),
        line("f,c22;2,c21,c22;2,e21,e20;f,e21;2,e21,c11;f,c11;f,c11;2,e11,c10;f,e10;f,e10;2,c00,e40;f,e40"),
        line("2,c22,e61;f,c22;f,e20;f,e20;f,c12;2,c11,c12;f,c10;f,c10;2,e10,t2;f,t2;f,e40;2,e40,c40"),
        line("f,e61;f,e61;f,t3;2,e20,t3;2,c12,e51;f,c12;2,c10,e50;f,e50;f,t2;2,t2,e90;f,c40;f,c40"),
        line("f,c62;2,e61,c62;2,t3,eA0;f,t3;f,e51;f,e51;f,e50;2,e50,c50;f,e90;f,e90;u,c40"),
        line("u,c62;f,c62;f,eA0;f,eA0;f,c52;2,e51,c52;f,c50;f,c50;u,e90"),
        "   " + line("u,eA0;u,c52;f,c52;u,c50"),
    ].join`\n`)
}

function renderMini(message = '') {
    function sticker(obj) {
        return colors[obj.color]; 
    }
    function crunch([l1, l2]) {
        return l1.map((d, i) => `{${d},${l2[i]}}▀{/}`).join``;
    }
    print([
        crunch([[sticker(cube.corners[3][0]),sticker(cube.corners[3][1]),sticker(cube.edges[0][1]),sticker(cube.corners[0][1]),sticker(cube.corners[0][0])],
        [sticker(cube.edges[3][0]),sticker(cube.edges[3][1]),sticker(cube.centres[0]),sticker(cube.edges[1][1]),sticker(cube.edges[1][0])]]) + ` ${message}`,
        crunch([[sticker(cube.corners[2][0]),sticker(cube.corners[2][1]),sticker(cube.edges[2][1]),sticker(cube.corners[1][1]),sticker(cube.corners[1][0])],
        [sticker(cube.corners[2][0]),sticker(cube.corners[2][2]),sticker(cube.edges[2][0]),sticker(cube.corners[1][2]),sticker(cube.corners[1][0])]]),
        crunch([[sticker(cube.edges[6][0]),sticker(cube.edges[6][1]),sticker(cube.centres[3]),sticker(cube.edges[5][1]),sticker(cube.edges[5][0])],
        [sticker(cube.corners[6][0]),sticker(cube.corners[6][2]),sticker(cube.edges[0xA][0]),sticker(cube.corners[5][2]),sticker(cube.corners[5][0])]]),
    ].join`\n`)
}


function mini() {
    doMoves(input);
    renderMini();
}

function view(view) {
    if (!view) {
        throw `usage: ${IRC.colors.cmd('cube.view', 'mini|normal')}`;
    }
    store.set(`${from}:view`, view);
    print(`Size for ${from} set to ${view}`);
}

function render(message) {
    if (store.get(`${from}:view`) === 'mini') {
        renderMini(message);
    } else {
        renderNormal(message);
    }
}

// solving / scramble / timer

const scrambler = IRC.require('scramble');

function scramble(type) {
    type = type.toUpperCase().trim();
    !['LL', 'LSE', 'TEST'].includes(type) && (type = '3x3');

    const scramble = {
        TEST: `RU`,
        LL: scrambler.getLLScramble(),
        LSE: `xy'` + scrambler.getLSEScramble() + ['', ' M2'][0|Math.random()*2],
    }[type] || (() => scrambler.getRandomScramble())();

    doMoves(scramble);

    store.save(`${from}:solve`, {
        moves: [],
        scrambleTime: +(new Date),
        startTime: undefined,
        endTime: undefined,
        scramble,
        type,
    });

    saveCube();
    render(`${type} scramble`);
}

const { intervalToDuration } = require('date-fns@2.28.0');

function apply(inputMoves) {
    loadCube();
    doMoves(inputMoves);
    saveCube();

    const state = store.load(`${from}:solve`, false);
    let message;
    if (state) {
        let { moves, startTime, ...other } = state;

        moves.push([+(new Date), inputMoves, IRC.message.text]);

        if (!startTime) {
            if (getMoves(inputMoves).some(move => !'xyz'.includes(move.source.move))) {
                // if there's a non inspection move
                startTime = +(new Date);
                message = 'timer started!';
            }
        }

        store.save(`${from}:solve`, {
            moves,
            startTime,
            ...other
        });


        if (isSolved()) {
            endTime = +(new Date);
            const duration = intervalToDuration({ start: new Date(startTime), end: new Date(endTime) });
            message = `solved in ${duration.minutes}:${duration.seconds}!`

        }
// ~cube.solution joey
// inspection time
        // scramble, solution
        // debug flag to see everything
//  notify of new pb / notify in channel

    }

    render(message);
}

if (IRC.command.path === 'cubetest2') {
    doMoves(input);
    render();
} else {
    module.exports = { cube, scramble, render, apply, getMoves, doMoves, mini, view };
}
