if (!module.required) {
    throw `usage: ${IRC.colors.cmd('lang', 'code')} valid repls are: bash ruby haskell go node deno perl python php jsx`;
}

module.exports = (lang, script) => {
    try { 
        response(fetchSync(`http://localhost:8010/${lang}`, {
            method: 'POST',
            body: script.replace(/\s\s+/g, '\n'),
        }));
    } catch(e) {
        throw 'down for maintenance';
    }
};

function response(res) {
    let [status, output] = JSON.parse(res);
    output = output.trim().replace(/ +/g, ' ').replace(/\r|\n/g, "â¤").slice(0, 390);
    if (!output) output = '[no output]';
    print(`{${status?'c':'r'}}>>{/} ${output}`);
}

