const REPLS = 'bash gcc g++ ruby lua vim haskell guile racket smalltalk elixir rust perl python php lolcode vlang';
const JS_REPLS = 'mozjs node deno quickjs jsx babel';

if (!module.required) {
    throw `usage: ${IRC.colors.cmd('[lang]', 'code')} valid repls are: ${REPLS} ${JS_REPLS}`;
}

module.exports = (lang, script) => {
    try { 
        // kirjavascript/eval needs to be running on the same server
        // it only accepts local requests
        response(fetchSync(`http://localhost:8010/${lang}`, {
            method: 'POST',
            body: script.replace(/␤|_nl_|##/g, '\n'),
        }));
    } catch(e) {
        throw 'down for maintenance';
    }
};

module.exports.REPLS = REPLS;
module.exports.JS_REPLS = JS_REPLS;

function response(res) {
    let [status, output] = JSON.parse(res);
    output = output.trim().replace(/ +/g, ' ').replace(/\r|\n/g, "␤");
    output = output.slice(0, 390);
    // output = output.slice(output.length - 390);
    if (!output) output = '[no output]';
    const prompt = IRC.colors(`{${status?'c':'r'}}>{/}`);
    print.raw(`${prompt} ${IRC.colors(parseColors(output))}`);
}

function parseColors(output) {
    return output
        .replace(new RegExp('\u001b\\[0m', 'g'), IRC.colors('{/}'))
        .replace(new RegExp('\u001b\\[31m', 'g'), IRC.colors('{r}'))
        .replace(new RegExp('\u001b\\[35m', 'g'), IRC.colors('{p}'))
        .replace(new RegExp('\u001b\\[36m', 'g'), IRC.colors('{dc}'))
        .replace(new RegExp('\u001b\\[38;5;11m', 'g'), IRC.colors('{y}'))
        .replace(new RegExp('\u001b\\[38;5;14m', 'g'), IRC.colors('{c}'))
        // catchall
        .replace(new RegExp('\u001b\\[(\\d+)m', 'g'), d => IRC.colors(IRC.colors.hash(d)))
}
