const daily = () => 0 === (0|new Date/1000)%86400;

const hourly = () => 420 === (0|new Date/1000)%3600;

IRC.listen('tick', ({ target }) => {
    if (target === '#8bitvape') {
      	if (daily()) IRC.require('7');
        else if (hourly()) print('7');
    }
});

