const miles = /\b(\d+\.?\d*|\.\d+) ?mi(les)?\b/;
const feet = /\b(\d+\.?\d*|\.\d+) ?(feet|ft)\b/;
const inch = /\b(\d+\.?\d*|\.\d+) ?inch(es)?\b/;
const yards = /\b(\d+\.?\d*|\.\d+) ?(yards|yd)\b/;

const km = /\b(\d+\.?\d*|\.\d+) ?(kilomet(re|er)s|km)\b/;
const cm = /\b(\d+\.?\d*|\.\d+) ?(centimet(re|er)s|cm)\b/;
const { wordsToNumbers } = require('words-to-numbers');

IRC.listen('message', (event) => {
    let { text } = event.message;
    text = wordsToNumbers(text);
    if (miles.test(text)) {
        const value = text.match(miles)[1];
        print(`${+(+value * 1.60934).toFixed(2)}km`);
    } else if (feet.test(text)) {
        const value = text.match(feet)[1];
        print(`${+(+value * 0.3048).toFixed(2)}m`);
    } else if (yards.test(text)) {
        const value = text.match(yards)[1];
        print(`${+(+value * 0.9144).toFixed(2)}m`);
    } else if (km.test(text)) {
        const value = text.match(km)[1];
        print(`${+(+value * 0.621371).toFixed(2)} miles`);
    } else if (cm.test(text)) {
        const value = text.match(cm)[1];
        print(`${+(+value * 0.393701).toFixed(2)} inches`);
    } else if (inch.test(text)) {
        const value = text.match(inch)[1];
        print(`${+(+value * 2.54).toFixed(2)}cm`);
    }
});
