const { nameToEmoji } = require('gemoji');
const { compareTwoStrings } = require('string-similarity');
const names = Object.keys(nameToEmoji);

const getEmoji = str => {
  	if (str === 'penis') return 'ð“‚¸';
    const list = names.map(d => [compareTwoStrings(d, str), d]).sort((a, b) => b[0] - a[0]);
    return nameToEmoji[list.shift().pop()];
};

IRC.listen('message', ({ message: { from, text } }) => {
  	if (/(?<!\d):([\w-]+?):/.test(text)) {
     	//print('ðŸ¤”'); 
      const result = text.toLowerCase().match(/:(\S*?):/g)
      if (result) {
        const out = result.map(d => getEmoji(d.slice(1,-1)));
        print(out.join(''));
      }
    }
});
