const name = require('hex-to-color-name');

const map = {
    red: 'FF0000',
    darkred: '800000',
    white: 'FFFFFF',
    black: '000000',
    rebeccablack: '221133',
    cyan: '00FFFF',
    darkcyan: '008080',
    blue: '0000FF',
    darkblue: '000080',
    green: '00FF00',
    darkgreen: '008000',
    magenta: 'FF00FF',
    purple: '800080',
    rebeccapurple: '663399',
    orange: 'FFA500',
    brown: '964B00',
    yellow: 'FFFF00',
    grey: 'AAAAAA',
    darkgrey: '666666',
};
const lookup = {
    red: 'r',
    darkred: 'dr',
    white: 'w',
    black: 'bl',
    rebeccablack: 'bl',
    cyan: 'c',
    darkcyan: 'dc',
    blue: 'b',
    darkblue: 'db',
    green: 'g',
    darkgreen: 'dg',
    magenta: 'p',
    purple: 'dp',
    rebeccapurple: 'dp',
    orange: 'o',
    brown: 'o',
    yellow: 'y',
    grey: 'gr',
    darkgrey: 'dgr',
};

const rx = /#([A-Fa-f0-9]{6}|[A-Fa-f0-9]{3})/;

IRC.listen('message', (event) => {
    if (rx.test(event.message.text)) {
        let [hex] = event.message.text.match(rx);
        if (hex.length === 4) {
            hex = `#${[...hex.slice(1)].map(d=>d+d).join``}`
        }
        const color = name(hex, map);
        print(`{${lookup[color]}}${color}`);
    }
});
