const dateFns = require('date-fns@1.30.1');

IRC.listen('tick', ({ target, server }) => {
    const print = global.print;
    IRC.setNamespace('remind');
    SQL.async.many`
        SELECT "key", "from", "time", "message" from remind
        WHERE "when" < ${(new Date).toISOString()}
        AND server = ${server}
        AND channel = ${target} 
        LIMIT 1
    `.then(messages => {
        if (messages.length > 0) {
            const [msg] = messages;

            let out = `${IRC.colors.nick(msg.from, true)} `;
            out += msg.message 
            out += ` {/}({dc}${dateFns.distanceInWordsStrict(msg.time, new Date())} ago{/})`;
            print(out);

            IRC.setNamespace('remind');
            SQL.async.run`
                DELETE FROM remind
                WHERE key = ${msg.key}
            `.catch(print.error);
        }
    }).catch(print.error);
}, { filter: () => IRC.queryConfig('enableRemind', true) })

IRC.setNamespace('remind');
if (!SQL.one`SELECT 1 FROM sqlite_master WHERE name='remind'`) {
    SQL.exec`
        DROP TABLE IF EXISTS remind;

        CREATE TABLE remind (
            key INTEGER PRIMARY KEY AUTOINCREMENT UNIQUE,
            "from" VARCHAR (100),
            "when" DATETIME,
            server TEXT,
            channel TEXT,
            message TEXT,
            time DATETIME DEFAULT ((DATETIME(CURRENT_TIMESTAMP, 'LOCALTIME')))
        );

        CREATE INDEX idx_from
        ON remind ('from', 'when');
    `;
}
