const rx = new RegExp('^((?:\\.|[^\\/])*)/((?:\\.|[^\\/])*)/?');

IRC.listen('message', (event) => {
    if (event.message.text.startsWith('s/')) {
        const m = event.message.text.slice(2).match(rx);

        if (m) {
            const [, search, replace] = m;
            const [item] = IRC.log.regex('^(?!s\\/).*?' + search, 1, 0);
            if (item) {
                const { user, message } = item;
                const replacement = `${IRC.colors.nick(IRC.breakHighlight(user), true)} ${message.replace(new RegExp(search), replace)}`;
                print(replacement);
            }
        }
    }
});
