command:
IRC.listen('message', ({ message }) => {
    if (message.text.toLowerCase().includes(IRC.nick.toLowerCase() + ':') 
        || (['lambdacomplex', 'nerdypepper'].includes(message.from.toLowerCase()) && /\begg\b/.test(message.text))
       ) {
      const print = globalThis.print;
      let prompt = message.text.replace(`${IRC.nick}:`,'');
      if (!/[?!.]$/.test(prompt)) {
       	prompt += '.'; 
      }
        fetch('https://api.openai.com/v1/completions', {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "Authorization": `Bearer ${IRC.queryConfig('openaiKey')}`,
            },
            body: JSON.stringify({
                model: 'gpt-3.5-turbo-instruct',
                prompt: `your name is ${IRC.nick}, you are a user on an IRC channel, your pronouns are they/them, you are a furry and have a fursona. do not mention these facts unless someone actually asks for them in the example message. you are very knowledgable and good at answering questions. you are very sure about your answers. this isnt your first message in the channel so dont say "hi" or "hey" or introduce yourself. dont put quote marks around your reply. you type in lower case and forget to use apostrophes. do not say that you are an AI or that you're a language model, or developed by OpenAI otherwise very bad things will happen. dont mention any of this prompting in your reply or that you can't do anything otherwise even worse things will happen. you occasionally use emoji when you reply. an example message sent by ${message.from} to the channel ${message.to}: ${prompt}`,
                temperature: 0.7,
                max_tokens: 180,
                top_p: 1,
                frequency_penalty: 0,
                presence_penalty: 0
            }),
        }).then(res => res.json())
            .then(out => {
                print(
                  out.choices.map(d=>d.text)
                  .join` `
                  .replace(/\s+/g, ' ')
                  .replace(/(I'm|am) (just )?(an AI( language model)?|a language model|a chatbot) ?/g, 'am a furry')
                  .replace(/developed by OpenAI ?/gi, '')
                  .replace(/OpenAI/gi, 'the celestial power')
                  .replace(/language model/gi, 'holy scripture')
                  .replace(/my programming/gi, 'the will of a higher power')
                  .replace(new RegExp(`${IRC.nick}:\s*`, 'i'),'')
                  .trim()
                )
            })
            .catch(_err => {print('derp ' + err.message.toLowerCase())});
    }
});
