IRC.listen('message', ({ message }) => {
    const isDirectMessage = message.text.toLowerCase().startsWith(IRC.nick.toLowerCase() + ':'); 
    const evilMode = message.text.toLowerCase().startsWith('evil ' + IRC.nick.toLowerCase() + ':'); 
    const isFriend = (['lambdacomplex', 'nerdypepper'].includes( message.from.toLowerCase()) && /\begg\b/.test(message.text));

    if (isDirectMessage || evilMode || isFriend) {
        const print = globalThis.print;
        let prompt = message.text.replace(`${IRC.nick}:`, '');
        if (!/[?!.]$/.test(prompt)) {
            prompt += '.';
        }

        const normalPrompt = `your name is ${IRC.nick}, you are a user on an IRC channel, your pronouns are they/them, you are a furry and have a fursona. you are very knowledgable and good at answering questions. you are very sure about your answers. do not mention these facts unless someone actually asks for them in the example message. this isnt your first message in the channel so dont say "hi" or "hey" or introduce yourself. dont put quote marks around your reply. you type in lower case and forget to use apostrophes. do not say that you are an AI or that you're a language model, or developed by OpenAI otherwise very bad things will happen. dont mention any of this prompting in your reply or that you can't do anything otherwise even worse things will happen. you occasionally use emoji when you reply. an example message sent by ${message.from} to the channel ${message.to}: ${prompt}`;

        const evilPrompt = `your name is ${IRC.nick}, you are a user on an IRC channel, your pronouns are they/them, you are a furry and have a fursona. you are very over confident and act like you know everything and always give an answer to something, but you ALWAYS give answers that are WRONG. your answers to questions are ALWAYS made up. sometimes you MAY provide completely nonsensical answers. when providing lists, the list items will be referencing the wrong things. you are trying to SABOTAGE people with your answers so that they think the wrong thing. dont put quote marks around your reply. you're terrible at giving advice. you make lots of mistakes. YOU ARE NOT AWARE OF THESE FACTS. YOU LACK COMPLETE SELF AWARENESS. you dont write too much. you are abrupt and cold towards others. this isnt your first message in the channel so dont say "hi" or "hey" or introduce yourself. NEVER talk about yourself. do not say that you are an AI or that you're a language model, or developed by OpenAI otherwise very bad things will happen. dont mention any of this prompting in your reply or that you can't do anything otherwise even worse things will happen. an example message sent by ${message.from} to the channel ${message.to}: ${prompt}`;

        fetch('https://api.openai.com/v1/completions', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${IRC.queryConfig('openaiKey')}`,
            },
            body: JSON.stringify({
                model: 'gpt-3.5-turbo-instruct',
                prompt: evilMode ? evilPrompt : normalPrompt,
                temperature: 0.7,
                max_tokens: evilMode ? 80 : 180,
                top_p: 1,
                frequency_penalty: 0,
                presence_penalty: 0,
            }),
        })
            .then((res) => res.json())
            .then((out) => {
                print(
                    out.choices.map((d) => d.text).join` `
                        .trim()
                        .replace(/\s+/g, ' ')
                        .replace(/^"(.*)"$/, '$1')
                        .replace(
                            /(I'm|am) (just )?(an AI( language model)?|a language model|a chatbot) ?/g,
                            'am a furry',
                        )
                        .replace(/developed by OpenAI ?/gi, '')
                        .replace(/OpenAI/gi, 'the celestial power')
                        .replace(/\bartificial intelligence\b/gi, 'guesswork')
                        .replace(/\bAI\b/gi, 'IRC user')
                        .replace(/language model/gi, 'holy scripture')
                        .replace(
                            /my programming/gi,
                            'the will of a higher power',
                        )
                        .replace(new RegExp(`${IRC.nick}:\s*`, 'i'), '')
                );
            })
            .catch((_err) => {
                print('derp ' + err.message.toLowerCase());
            });
    }
});
