(async () => {
    if (Number(input) > 1000) {
        return print('pls that number is going to be huge');
    }
    const BN = await acquire('bn.js@4.11.8');
    function ctz32(t) {
        return 31 - Math.clz32(t ^ (t - 1));
    }
    function factorial(t) {
        const n = new BN(1);
        let c = 1,
            o = 0,
            r = 0;
        for (let e = 1; e <= t; ++e) {
            let t = e;
            const l = ctz32(t);
            for (o += l, t >>= l; !(t % 5); ) (o -= 1), (r += 1), (t /= 5);
            const i = c * t;
            i < 67108864 ? (c = i) : (n.imuln(c), (c = t));
        }
        return n.imuln(c), n.ishln(o), n.toString() + '0'.repeat(r);
    }
    print(`factorial(${input}) = ${factorial(input)}`);
})();
