command:
const { getLeaderboard } = IRC.require('golf.store');
const { getTask, taskList, taskListDisplay } = IRC.require('golf.tasks');
const leaderboard = IRC.require('module.leaderboard');

const [type, modifiers = ''] = (input.trim() || IRC.message.from).split` `;
const showAll = modifiers.includes('all');
const showPosition = modifiers.includes('position');
if (type === 'global') throw 'coming soon';

const task = getTask(type);

if (task) {
    const scores = getLeaderboard(type)
        .map(entry => ({
            score: entry.bytes,
            name: entry.scores.map(d => d.from).join`, `,
            entry,
        }));

    const publicStr = task.public ? ' {dc}[PUBLIC]{/}' : '';
    const hasPublicScore = scores[0] && scores[0].name === '##jsgolf';
    const fmtRank = ({ entry: { position, rank }}, index) => { 
        const value = showPosition ? position : rank;
        return hasPublicScore ? value - 1 : value;
    }

    print(leaderboard({
        scores,
        title: `Leaderboard - ${IRC.colors.nick(task.displayName, !1)}${publicStr}`,
        reverse: true,
        limit: showAll ? Infinity : 5,
        fmtRank,
    }));
} else {
    const userScores = [];
    const user = type.toLowerCase();
    taskList.map(key => getLeaderboard(key))
        .forEach((board, i) => {
            const key = taskListDisplay[i];
            const wr = board.length ? board[0].bytes : 0;
            board.forEach(entry => {
                if (entry.scores.find(score => score.from.toLowerCase() === user)) {
                    entry.wrRatio = entry.bytes / wr;
                    userScores.push([key, entry]);
                }
            });
        });

    if (!userScores.length) {
        throw 'no task by this name / no scores for this user'
    }

    const avgRank =  +(userScores.reduce((a, b) => a + b[1].rank, 0) / userScores.length).toFixed(2);
    const avgPos = +(userScores.reduce((a, b) => a + b[1].position, 0) / userScores.length).toFixed(2);
    const baseRatio = +(userScores.reduce((a, b) => a + b[1].wrRatio, 0) / userScores.length).toFixed(2);
    const wrs = userScores.filter(d => d[1].rank === 1).length;
    const scores = userScores.map(([taskName, { bytes, position, rank }]) => {
        return `${IRC.colors.nick(taskName, false)}:{y}${bytes}{/}:{g}${rank}{/}:{dg}${position}{/}`;
    }).join` `;
    const desc = '{bo}task{/}:{y}score{/}:{g}rank{/}:{dg}position{/}';
    const wrString = wrs ? ` {bo}World Records:{/} {dc}${wrs}{/}` : '';
    print(`{bo}Tasks Complete:{/} {dc}${userScores.length}/${taskList.length}{/} {dc}${baseRatio}{/} {bo}Avg Rank:{/} {dc}${avgRank}{/} {bo}Avg Position:{/} {dc}${avgPos}{/}${wrString} {bo}Avg WR Ratio:{/}  (${desc})`);
    print(scores);
}
