const { getScores, setScores } = IRC.require('golf.store');
const { getTask } = IRC.require('golf.tasks');

const [taskName, flags, flags2] = input.trim().split` `
const task = getTask(taskName);
const scores = getScores(taskName);

if (task.public) {
    const name = flags || IRC.message.from;
    const found = scores[name];
    if (!found) {
        throw `no solution for ${taskName} by ${name}`;
    } else {
        if (flags2 === 'pastebin') {
            print(IRC.require('module.paste')(found.solution));
        } else {
            print.raw(`${found.solution} `+ IRC.colors(`({dc}${found.bytes}{/})`));
        }
    }
} else {
    IRC.auth();
    if (flags !==  undefined && flags !== '--force') {
        throw `task is not public`;
    }
    const found = scores[IRC.message.from];
    if (found) {
        if (IRC.message.to == '##jsgolf' && flags !== '--force') {
            throw 'cannot send to channel (use --force to force)';
        } 
        print.raw(`${found.solution} `+ IRC.colors(`({dc}${found.bytes}{/})`));
    } else {
        throw `no solution for ${taskName}`;
    }
}
