command:
IRC.auth();

const { getTask } = IRC.require('golf.tasks');
const { ordinal, plural, byteCount } = IRC.require('golf.util');
const { getLeaderboard, getScores, setScores } = IRC.require('golf.store');

const [taskName, ...solutionList] = input.split` `;
const solution = solutionList.join` `;
const bytes = byteCount(solution);
const task = getTask(taskName);

const isPrivate = IRC.message.to !== '##jsgolf';
const from = task.public ? '##jsgolf' : IRC.message.from;

if (!task.public && !isPrivate) throw 'submit private tasks via PM';

task.test(solution);
const scores = getScores(taskName);
const wrBytes = Math.min(...Object.values(scores).map(d => d.bytes));

if (task.public) {
    if (bytes >= wrBytes) {
        throw '{y}correct{/}, but there is already a score of {dc}' + wrBytes;
    }
}

if (scores[from] == undefined) { 
    scores[from] = {
        bytes,
        solution,
        timestamp: (new Date).toISOString(),
        history: [],
    };
    setScores(taskName, scores);
    isPrivate && print(`{g}correct!{/} your score is {dc}${bytes}{/}`);
    notifyChannel({ from, bytes, taskName, saved: task.public ? wrBytes - bytes : 0 });
} else {
    if (scores[from].bytes < bytes) {
        isPrivate && print('{y}correct{/}, {dc}' +bytes + '{/}. but you have a better score of {dc}'+ scores[from].bytes + '{/}')
    } else {
        const oldScore = scores[from].bytes;

        if (oldScore === bytes) {
            isPrivate && print('{y}correct{/}, but you already have a score of {dc}' +bytes + '{/} {gr}(your solution has been updated){/}')
            if (
                scores[from].solution !== solution
                && !scores[from].history.includes(scores[from].solution)
            ) {
                scores[from].history.push(scores[from].solution);
            }
            scores[from].solution = solution;
            setScores(taskName, scores);
        } else {
            const saved = oldScore - bytes;
            isPrivate && print(`{g}correct!{/} you saved {dc}${saved}{/} ${plural('byte', saved)} for a new score of {dc}${bytes}{/}`);
            const { history, solution: oldSolution } = scores[from];
            history.push(oldSolution);
            scores[from] = {
                bytes,
                solution,
                timestamp: (new Date).toISOString(),
                history,
            };
            setScores(taskName, scores);
            notifyChannel({ from, bytes, taskName, saved });
        }
    }
}

function notifyChannel({ bytes, taskName, from, saved }) {
    const leaderboard = getLeaderboard(taskName);
    const { rank, position, scores } = leaderboard.find(d => d.bytes === bytes);

    const colours = ['{y}','{gr}','{o}'];
    const wr = rank === 1 && scores.length === 1;
    const rankStr = rank < 4
        ? `  (${colours[rank-1]||'{bo}'}${ordinal(rank)} place${[...'ðŸ¥‡ðŸ¥ˆðŸ¥‰'][rank-1]}{/})`
        : '';
    const wrStr = wr ? ' {g,bl}{rb} World Record! {/}' : '';

    const who = IRC.colors.nick(IRC.message.from, true);
    const action = saved 
        ? `saved {dc}${saved}{/} ${plural('byte', saved)} for a new score of {dc}${bytes}{/}`
        : `got a score of {dc}${bytes}{/}`;
    const location = `on ${IRC.colors.nick(taskName, false)}`;
    const annotations = `${rankStr}${wrStr}`;

    const target = '##jsgolf';
    if (task.public) {
        print(`${who} ${action} ${location} for {y}##jsgolf{/}!`, { target });
    } else {
        print(`${who} ${action} ${location} ${annotations}`, { target })
    }
}
