const { inspect } = IRC;

const stringify = (obj) => {
    return inspect(obj, {
        colors: false,
        depth: 0,
        truncate: 100,
    });
};

function removeEval() {
    const _eval = eval;
    const _function = Function;
    const stub = eval = Function = function(){throw 'eval disabled'};
    const _fns = [
        function(){},
        async function(){},
        function*(){},
        async function*(){},
    ];
    const _constructors = _fns.map(fn => {
        const proto = Object.getPrototypeOf(fn);
        const _constructor = proto.constructor;
        delete proto.constructor;
        proto.constructor = stub;
        return [_constructor, proto];
    });
    return () => {
        eval = _eval;
        Function = _function;
        _constructors.forEach(([c, p], i) => {
            p.constructor = c;
        });
    };
}

function assertType(a, type) {
    if (typeof a !== type) {
        throw new Error(`expected {dc}${type}{/} got {o}${typeof a}{/}`);
    }
}

function assertEq(a, b) {
    if (!isEqual(a, b)) {
        throw new Error(`expected {dc}${stringify(b)}{/} got {o}${stringify(a)}{/}`);
    }
}
function assertCall(result, expected, ...args) {
    const output = result(...args);
    if (!isEqual(expected, output)) {
        const params = args.map(stringify).join`, `;
        throw new Error(`expected {dc}${stringify(expected)}{/} got {o}${stringify(output)}{/}  (params: {dp}${params}{/})`);
    }
}

