function setSize(text, size) {
    const sizes = {
        1: small,
        3: medium,
        4: large,
    };
    if (sizes[+size]) {
        return sizes[+size](text);
    }
    return text;
}

function getColor(color) {
    function dark(color, s) {
        return '{' + (/night|dark/.test(color) ? 'd' + s : s) + '}';
    }
    color = color ? color.trim() : '';
    if (color[0] == '#') {
        try {
            color = findClosestColor(color).name
            // log(color);
        } catch {}
    }
    if (/red|maroon/.test(color)) {
        return dark(color, 'r');
    } else if (/white/.test(color)) {
        return '{w}';
    } else if (/black/.test(color)) {
        return '{bl}';
    } else if (/cyan/.test(color)) {
        return dark(color, 'c');
    } else if (/blue/.test(color)) {
        return dark(color, 'b');
    } else if (/green/.test(color)) {
        return dark(color, 'g');
    } else if (/purple|pink|indigo/.test(color)) {
        return dark(color, 'p');
    } else if (/brown|orange/.test(color)) {
        return '{o}';
    } else if (/yellow/.test(color)) {
        return '{y}';
    } else if (/grey|gray/.test(color)) {
        return '{gr}';
    } else if (/rainbow/.test(color)) {
        return '{rb}';
    }
    return '';
}

