command:
throw '@deprecated, use ~whois and update config instead'
const [cmd, user] = input.trim().split(/\s+/);
if (!cmd || !user) {
    throw (`usage: ${IRC.colors.cmd('ignore', ['add/remove', 'nick'])}`)
}
const { node } = IRC.sudo();
const admins = node.admins.get() || [];


        const data = IRC.whois(user);
        if (!data.host) {
            throw new Error(`${user} not found`);
        }
        if (admins.includes(user)) {
            throw new Error('do not do this');
        }
        const { host } = data;
        const hostList = node.config.['ignoreHosts'].get() || [];
        if (cmd == 'add') {
            hostList.push(host);
            node.config.['ignoreHosts'].set(hostList)
            print.info(`${user} has been ignored`);
        } else if (cmd == 'remove') {
            node.config.['ignoreHosts'].set(hostList.filter(d => d != host));
            print.info(`${user} has been unignored`);
        }
