function renderLogs(list) {
    return list.map(
        ({ time, user, command, target, message }) =>
            IRC.colors(
                `({dc}{u}${time}{/}) {bo}<{/}${IRC.colors.hash(
                    user,
                )}${IRC.breakHighlight(user)}${
                    command == 'PRIVMSG' ? '' : ':' + command.toLowerCase()
                }{/}{bo}>{/}`,
            ) +
            '\u2800' +
            `${message.replace(/\n/g, ' ')}`,
    );
}

function printLogs(list) {
    renderLogs(list).forEach((d) => print.raw(d, { log: false }));
}

