function rollDice() {
  return 1 + Math.floor(Math.random() * 6);
}

function playRound() {
  const dice1 = rollDice();
  const dice2 = rollDice();
  const sum = dice1 + dice2;
  print(`You rolled a {g}${dice1}{/} and a {g}${dice2}{/}. Sum: {o}${sum}`);

  if (sum === 7) {
    print("{rb}Congratulations! You hit Lucky 7!");
  } else {
    print("{r}Unlucky! Didn't hit 7 this time.");
  }
}

playRound()
