command:
if (!IRC.command.params[0] || !input.trim()) {
    throw new SyntaxError(`{p}${IRC.trigger}mem贸(name){/} [message] or {p}${IRC.trigger}mem贸(name, time){/} [message]`)
}
else {
    const now = new Date();
    const [name, time] = IRC.command.params;
    const timestamp = time ? IRC.parseTime(time) : now;
    const dateFns = require('date-fns@1.30.1');const distance = dateFns.distanceInWordsStrict(timestamp, now);
    if (dateFns.isBefore(timestamp, now)) {
        throw new Error(`epoch fail - requested time is ${distance} ago`);
    }
    IRC.eventFns.addEvent('speak', {
        callback: 'mem贸.event',
        time: timestamp,
        message: `Jablinksi Gaming`,
        target: name.trim(),
    });
    print(`Saved message for ${IRC.colors.nick(name, false)}` + (timestamp.getTime() == now.getTime() ? '' : ` in {o}${distance}{/} on {dc}${dateFns.format(timestamp, 'dddd MMMM Do YYYY HH:mm:ss')}{/}`))
}
