command:
const [name] = IRC.command.params;

if (!name || !input.trim()) throw `usage: ${IRC.colors.cmd('memo', 'message', 'name')}`;

SQL.run`
    INSERT INTO memo ("from", "to", server, channel, message) 
    VALUES (${IRC.message.from}, ${name}, ${IRC.server}, ${IRC.channel}, ${input})
`;

print(`Saved message for ${IRC.colors.nick(name, false)}`);
