const exists = !!SQL.one`SELECT 1 FROM sqlite_master WHERE type='table' AND name='memo';`;


if (true||!exists) {
    SQL.exec`
        DROP TABLE IF EXISTS memo;

        CREATE TABLE memo (
            key INTEGER PRIMARY KEY AUTOINCREMENT UNIQUE,
            \`from\` VARCHAR (100),
            \`to\` VARCHAR (100),
            address TEXT,
            channel TEXT,
            message TEXT,
            time DATETIME DEFAULT ((DATETIME(CURRENT_TIMESTAMP, 'LOCALTIME')))
        );

        CREATE INDEX idx_to
        ON memo ('to');
    `;
}

