// event handler for memo

if (!IRC.event) {
    throw new Error('you cannot call this command manually')
}

const { type, message, user, target, init, timestamp } = IRC.event;
const isSelf = user.toLowerCase() == target.toLowerCase();
let out = `${target}: `;
if (!isSelf) {
    out += `${IRC.colors.nick(user, true)} `;
}
out += message + ` {/}({dc}${dateFns.distanceInWordsStrict(init, new Date())} ago{/})`;
print(out);
