const names = IRC.command.params;

if (!names.length || !input.trim()) throw `usage: ${IRC.colors.cmd('memo', 'message', ['name1', 'name2', '...'])}`;


names.forEach(name => {
    SQL.run`
        INSERT INTO memo ("from", "to", server, channel, message) 
        VALUES (${IRC.message.from}, ${String(name)}, ${IRC.server}, ${IRC.channel}, ${input})
    `;
});


print(`Saved message for ${names.map(name => IRC.colors.nick(name, false)).join(', ')}`);
