const _ = require('lodash');if (!IRC.command.params.length || !input.trim()) {
    throw new SyntaxError(`{p}${IRC.trigger}memo.multi(name1,name2,name3){/} [message]`)
}
else {
    const now = new Date();
    const users = _.uniq(IRC.command.params.map(d => d.trim()));

    users
    .forEach(name => {
        IRC.eventFns.addEvent('speak', {
            callback: 'memo.event',
            time: now,
            message: input,
            target: name,
        });
    });

    const namesRender = users.map(d => `${IRC.colors.hash(d)}${d}{/}`).join`, `;

    print(`Saved message for ${namesRender}`)
}
