if (IRC.command.params[0] === 'auth' || !IRC.setNick(input)) {
    const str = input.replace(/[^a-zA-Z0-9]+/g, '');
    const sudo = IRC.sudo();
    sudo.node.client.send.call('NICK', str);
    // reauth in case we got deauthed for whatever reason
    if (str.toLowerCase() == sudo.node.config.nickname.get().toLowerCase()) {
        sudo.node.client.say.call('nickserv', `identify ${sudo.node.password.get()}`);
    }
}
