(function() {
    const cmds = input.split('|').map(d => d.trim());
    global.input = IRC.parseCommand({ text: cmds[0] }).input;
    let data = ''
    const [_print, _log] = [print, print.log];

    ['print', 'notice', 'action'].forEach(printer => {
        global[printer] = (str) => {
            // _log(str);
            data += str;
        };

        global[printer].raw = (str) => {
            // _log(str);
            data += str;
        };
    });

    cmds.forEach(cmd => {
        // _log([data, input, cmd]);
        IRC.command = IRC.parseCommand({ text: cmd });
        // _log([global.input, IRC.command.path])
        IRC.require(IRC.command.path);
        global.input = data;
        data = ''
    })

    _print(input);

})()
