const _ = require('lodash');store.all().forEach(({key}) => store.set(key, undefined))
store.all().forEach(({key, value}) => print(`Quote ${key}: ${value}`))

getQuote = (num = null) => {
    if(num){
        const q = store.get(num)
        if(q){
            return q
        }else{
            throw new Error(`can't find that quote`)
        }
    }else{
        return _.shuffle(store.all())[0]
    }
}

if(input){
    const count = store.all().length
    print(count)
    store.set(parseInt(store.all()[store.all().length]+1), input)
    print(`Quote ${count} added: ${input}`)
}else if(IRC.command.params[0]){
    const quote = getQuote(IRC.command.params[0])
    print(`Quote ${quote.key}: ${quote.value}`)
}else{
    const quote = getQuote()
    print(`Quote ${quote.key}: ${quote.value}`)
}
