command:
const { from } = IRC.message

let player = store.load(from)

if (! player.status) {
  throw new Error('Use ~r7rpg.register to play') 
}

if (player.status.type === 'dead') {
  throw new Error(`{dc}${from}{/} cannot battle (Status: ${player.status.type})`)
}

if (player.status.type === 'idle') {
  if(IRC.command.params[0]) {
    IRC.require('r7rpg.enemies').clonePlayer(IRC.command.params[0])
    IRC.require('r7rpg.game').incrementInfo('enemies_battled')
    player = store.load(from)
  } else {
    IRC.require('r7rpg.enemies').newEnemy()
    IRC.require('r7rpg.game').incrementInfo('enemies_battled')
    player = store.load(from)
  }
}

if (player.status.type != 'combat') {
  throw new Error(`You cannot battle (Status: ${player.status.type})`)
}

if (player.status.type === 'combat') {
  IRC.require('r7rpg.game').incrementInfo('player_attacks')
  print(`{dc}${from}{/} ({p}lvl: ${player.stats.level}{/} | {r}hp: ${player.stats.current_health}/${player.stats.max_health}{/}) is battling a {g}${player.enemy.name}{/} ({p}lvl: ${player.enemy.level}{/} | {r}hp: ${player.enemy.health}{/})`)
  const attack = IRC.require('r7rpg.player').getAttack()
  IRC.require('r7rpg.player').damageEnemy(attack)
}
