command:
const { from } = IRC.message

let player = store.load(from)
let shop = store.load('#shop')

if (! player.status) {
  throw new Error('Use ~r7rpg.register to play') 
}

if (player.status.type === 'idle') {
  let amount = IRC.command.params[0] ?? 1
  let shopItem = IRC.require('r7rpg.shop').getShopItem()
  let item, price, verbose
  switch(String(input)) {
    case 'Potion':
    case 'Potions':
      item = 'Potion'
      price = 100 * amount
      verbose = false
      break
    case 'Beans':
      item = input
      price = 1 * amount
      verbose = false
      break
    case 'Item':
    case 'Items':
      item = "Item"
      price = 1000 * amount
      verbose = true
      break
    default:
      if (String(input) === String(shopItem.name)) {
        amount = 1
        item = shopItem.name
        price = shopItem.price
        verbose = true
        break
      } else {
      	throw new Error(`Use {y}~r7rpg.buy{/} item name. {y}~r7rpg.shop list{/} to list items`)
      }
  }
  if(amount && ! isNaN(amount) && amount > 0 && amount != 1) {
    if (player.stats.money >= price) {
      IRC.require('r7rpg.player').charge(price)
      for (i = 0; i < amount; i++) {
        if (item === "Item") {
          IRC.require('r7rpg.player').giveItem(null, '{b}Shop{/}', null, verbose)
        } else {
          IRC.require('r7rpg.player').giveItem(item, '{b}Shop{/}', null, verbose)
        }
      }
      if (item === 'Potion') {
        IRC.require('r7rpg.game').incrementInfo('potions_bought', amount)
      }
      if (item === 'Item') {
        IRC.require('r7rpg.game').incrementInfo('items_bought', amount)
      }
      print(`{dc}${from}{/} bought {p}${item}{y}*${amount}`)
    } else {
      throw new Error(`{y}${from}{/} is too poor to buy {p}${item}{y}*${amount}`)
    }
  } else {
    if (player.stats.money >= price) {
      IRC.require('r7rpg.player').charge(price)
      if (item === 'Item') {
        IRC.require('r7rpg.player').giveItem(null, '{b}Shop{/}')
      } else {
        IRC.require('r7rpg.player').giveItem(item, '{b}Shop{/}')
      }
      if (item === 'Potion') {
        IRC.require('r7rpg.game').incrementInfo('potions_bought')
      } else if (item === null || item === 'Beans') {
        IRC.require('r7rpg.game').incrementInfo('items_bought')
      } else {
        IRC.require('r7rpg.game').incrementInfo('items_bought')
        IRC.require('r7rpg.shop').newShopItem()
      }
    } else {
      throw new Error(`{y}${from}{/} is too poor to buy {p}${item}{/}`)
    }
  }
} else {
  throw new Error(`{dc}${from}{/} cannot shop (Status: ${player.status.type})`)
}
