const { from } = IRC.message
let player = getPlayer()
let game = store.load('#game')

function getPlayer(_from = null) {
  if (_from) {
	let player = store.load(_from)
    if (! player.status) {
      throw new Error(`Player {dc}${_from}{/} not found`) 
    }
    return player 
  } else {
    let player = store.load(from)
    if (! player.status) {
      throw new Error(`Player {dc}${from}{/} not found`) 
    }
    return player 
  }
}

function savePlayer(_player) {
  store.save(from, _player)
}

function savePlayerAs(_from, _player) {
  store.save(_from, _player)
}

const roll7 = (fancy = false) => {
  incrementInfo('roll7s')
  const roll = () => (0|Math.random()*6)+1
  const dice = Array.from({length: 7}, roll)
  const total = dice.reduce((a, c) => a + c, 0)
  if (player.items.includes("Red Dice")) {
    fancy = true
  }
  let render
  if (fancy) {
    render = '{gr,dr} ' + dice.map(d => d == 1 ? '{bo}{w,dr}1{/}{gr,dr}' : d).join` {/} {gr,dr} ` + ' {/}'
  } else {
    render = '{bl,gr} ' + dice.map(d => d == 1 ? '{bo}{p,gr}1{/}{bl,gr}' : d).join`-` + ' {/}';
  }
  IRC.require('r7rpg.player').bestLowest('roll7', total, false)
  IRC.require('r7rpg.player').bestHighest('roll7worst', total, false)
  bestLowest('roll7', total, true)
  bestHighest('roll7worst', total, true)
  let percent
  if (roll === 7) {
    print(`{rb}wtf a 7 has been rolled`)
    percent = 1000000
  } else {
    percent = Math.ceil(((total-7)/35)*100)
  }
  return {total: total, render: render, percent: percent}
}

saveGame = () => {
  store.save('#game', game)
}

const setInfo = (key, value) => {
  if (game.info != undefined && game.info[key] != undefined) {
    game.info[key] = value
  } else {
    if (game.info == undefined) {
      game.info = {}
      game.info[key] = value
    } else {
      game.info[key] = value
    }
  }
  saveGame()
  return game.info[key]
}

const getInfo = (key) => {
  if (game.info != undefined && game.info[key] != undefined) {
    return game.info[key]
  } else {
    return 0
  }
  saveGame()
}

const incrementInfo = (key, value = 1) => {
  if (game.info != undefined && game.info[key] != undefined) {
    game.info[key] += value
  } else {
    if (game.info == undefined) {
      game.info = {}
      game.info[key] = value
    } else {
      game.info[key] = value
    }
  }
  saveGame()
  return game.info[key]
}

const bestLowest = (key, value, verbose = false) => {
  if (game.info != undefined && game.info[key] != undefined) {
    if (game.info[key] > value) {
      game.info[key] = value
      game.info[key + '_player'] = from
      if (verbose) {
        print(`{dc}${from}{/} got a new {b}${key}{/} record of {y}${value}`)
      }
    }
  } else {
    if (game.info == undefined) {
      game.info = {}
      game.info[key] = value
    } else {
      game.info[key] = value
    }
  }
  saveGame()
}

const bestHighest = (key, value, verbose = false) => {
  if (game.info != undefined && game.info[key] != undefined) {
    if (game.info[key] < value) {
      game.info[key] = value
      game.info[key + '_player'] = from
      if (verbose) {
        print(`{dc}${from}{/} got a new {b}${key}{/} record of {y}${value}`)
      }
    }
  } else {
    if (game.info == undefined) {
      game.info = {}
      game.info[key] = value
    } else {
      game.info[key] = value
    }
  }
  saveGame()
}


module.exports = {
    getPlayer, savePlayer, savePlayerAs, roll7, setInfo, getInfo, incrementInfo, bestLowest, bestHighest
};
