const { from } = IRC.message

let player_name = from
let player = store.load(from)

if (! player.status) {
  throw new Error(`Player {dc}${name}{/} not found. Use {y}~r7rpg.register{/} to play.`) 
}

const savePlayer = () => {
  store.save(player_name, player)
}

const addXp = (xp, verbose = false) => {
  IRC.require('r7rpg.game').incrementInfo('xp_added', xp)
  bestHighest('xpReceived', xp, false)
  player.stats.xp += xp
  if (verbose) {
    print(`{dc}${player_name}{/} gained experience {g}(+${xp}xp){/} {r}(${player.stats.xp}/${player.stats.next_level}){/}`)
  }
  while (true) {
    if (player.stats.xp > player.stats.next_level) {
      levelUp()
    } else {
      break
    }
  }
  savePlayer()
}

const addHealth = (health, heal = false, verbose = true) => {
  player.stats.max_health += health
  if (heal) {
    healPlayer(false, false, false)
  }
  if (verbose) {
    if (health > 0) {
      print(`{dc}${player_name}{/} feels healthier {g}(+${health}){/} {r}(${player.stats.current_health}/${player.stats.max_health}){/}`)
    } else {
      print(`{dc}${player_name}{/} feels unhealthy {r}(${health}){/} {r}(${player.stats.current_health}/${player.stats.max_health}){/}`)
    }
  }
  savePlayer()
}

const healPlayer = (amount = false, overflow = false, verbose = true) => {
  if (amount) {
    if (overflow) {
      player.stats.current_health += amount
    } else {
      healAmount = player.stats.max_health - player.stats.current_health
      if (healAmount <= amount) {
        amount = healAmount
      }
      player.stats.current_health += amount
    }
  } else {
    amount = player.stats.max_health - player.stats.current_health
    player.stats.current_health = player.stats.max_health
  }
  IRC.require('r7rpg.game').incrementInfo('times_healed')
  if (verbose) {
    print(`{dc}${player_name}{/} healed ${amount} hp ({r}${player.stats.current_health}/${player.stats.max_health}{/})`)
  }
  savePlayer()
}

const addStrength = (strength, verbose = true) => {
  player.stats.strength += strength
  if (verbose) {
    if (strength > 0) {
      print(`{dc}${player_name}{/} feels stronger {g}(+${strength} strength){/}`)
    } else {
      print(`{dc}${player_name}{/} feels weaker {r}(${strength} strength){/}`)
    }
  }
  savePlayer()
}

const addDefense = (defense, verbose = true) => {
  player.stats.defense += defense
  if (verbose) {
    if (defense > 0) {
      print(`{dc}${player_name}{/} defense has increased {g}(+${defense} defense){/}`)
    } else {
      print(`{dc}${player_name}{/} defense has decreased {r}(${defense} defense){/}`)
    }
  }
  savePlayer()
}

const addAgility = (agility, verbose = true) => {
  player.stats.agility += agility
  if (verbose) {
    if (agility > 0) {
      print(`{dc}${player_name}{/} agility has increased {g}(+${agility} agility){/}`)
    } else {
      print(`{dc}${player_name}{/} agility has decreased {r}(${agility} agility){/}`)
    }
  }
  savePlayer()
}

const addIntelligence = (intelligence, verbose = true) => {
  player.stats.intelligence += intelligence
  if (verbose) {
    if (intelligence > 0) {
      print(`{dc}${player_name}{/} feels smarter {g}(+${intelligence} intelligence){/}`)
    } else {
      print(`{dc}${player_name}{/} feels dumber {r}(${intelligence} intelligence){/}`)
    }
  }
  savePlayer()
}

const levelUp = () => {
  IRC.require('r7rpg.game').incrementInfo('levels_added')
  player.stats.level++
  player.stats.next_level = Math.ceil(player.stats.next_level + (player.stats.next_level * 1.6))
  addStrength(player.stats.level * 5, false)
  addHealth(10, true, false)
  addDefense(10, false)
  addAgility(10, false)
  addIntelligence(10, false)
  print(`{rb}${player_name} leveled up! (lvl ${player.stats.level}) (XP: ${player.stats.xp}/${player.stats.next_level}){/}`)
  savePlayer()
  if (player.items.includes("Lucky Dog's Dick")) {
    IRC.require('r7rpg.items').getItem()
    if (incrementItemInfo('luckyDogDickUses') > 20) {
      removeItem('Lucky Dog\'s Dick')
      setItemInfo('luckyDogDickUses', 0)
      print(`{dc}${from} has worn his {y}Lucky Dog's Dick{/} right out`)
    }
  } else if (Math.floor(Math.random() * 3) === 2) {
    IRC.require('r7rpg.items').getItem()
  }
}

const getAttack = (defendable = true) => {
  roll = IRC.require('r7rpg.game').roll7()

  let attack = Math.ceil(player.stats.strength - (player.stats.strength/100) * roll.percent)

  if (defendable && defChance(player.enemy.intelligence, player.stats.intelligence)) {
    defenseRoll = IRC.require('r7rpg.game').roll7()
    defense = Math.ceil(player.enemy.defense - (player.enemy.defense/100) * defenseRoll.percent)
    attack -= defense
    if (attack < 0) {
      attack = 0
    }
    print(`${roll.render} {y}*${roll.total}*{/} {dc}${from}{/} did {b}${attack}{/} ({r}-${defense} def{/}) damage`);
  } else {
    print(`${roll.render} {y}*${roll.total}*{/} {dc}${from}{/} did {b}${attack}{/} damage`);
  }

  bestHighest('damageGiven', attack, false)
  IRC.require('r7rpg.game').bestHighest('most_damage_given', attack)
  IRC.require('r7rpg.game').incrementInfo('damage_given', attack)

  return attack
}

const damage = (amount, verbose = true) => {
  player.stats.current_health -= amount
  bestHighest('damageTaken', amount, false)
  IRC.require('r7rpg.game').bestHighest('most_damage_taken', amount)
  IRC.require('r7rpg.game').incrementInfo("damage_taken", amount)
  if (player.stats.current_health <= 0) {
    IRC.require('r7rpg.items').onBattleEnd()
    kill()
  }
  if (player.stats.current_health > 0 && player.stats.current_health < 20 && verbose) {
    print(`{dc}${from}{/}{r} is badly wounded{/}`);
  }
  savePlayer()
}

const kill = (verbose = true) => {
  player.status.type = 'dead'
  const d = new Date();
  const time = d.toISOString();
  player.status.last_death = time
  player.stats.current_health = 0
  player.enemy = null
  incrementInfo("deaths")
  IRC.require('r7rpg.game').incrementInfo('deaths')
  if (verbose) {
    print(`{dc}${from}{/} has died`);
  }

  if (player.items.includes("Auto Defibrillator")) {
    revive()
    if (incrementItemInfo('autoDefibrillatorUses') > 100) {
      removeItem('Auto Defibrillator')
      setItemInfo('autoDefibrillatorUses', 0)
      print(`{dc}${from}'s{y} Auto Defibrillator{/} has worn out`)
    }
    if (player.items.includes("Rusty Spanner")) {
      healPlayer()
      if (incrementItemInfo('rustySpannerUses') > 100) {
        removeItem('Rusty Spanner')
        setItemInfo('rustySpannerUses', 0)
        print(`{dc}${from}'s{y} Rusty Spanner{/} has worn out`)
      }
    }
  }
  savePlayer()
}

const revive = (verbose = true) => {
  if (player.status.type === 'dead') {
    player.status.type = 'idle'
    IRC.require('r7rpg.game').incrementInfo("revives")
    if (verbose) {
      print(`{dc}${from}{/} is alive`);
    }
	savePlayer()
  }
}

const charge = (amount, verbose = true) => {
  IRC.require('r7rpg.game').incrementInfo("money_spent", amount)
  if (player.stats.money >= amount) {
    player.stats.money -= amount
    if (verbose) {
      print(`{dc}${player_name}{/} spent {r}$${amount}{/} ({y}$${player.stats.money}{/})`)
    }
    savePlayer()
    return true
  } else {
    throw new Error(`{dc}${from}{/} is too poor`)
  }
}

const credit = (amount, verbose = false) => {
  IRC.require('r7rpg.game').incrementInfo("money_given", amount)
  player.stats.money += amount
  if (verbose) {
    print(`{dc}${player_name}{/} received {y}$${amount}{/} ($${player.stats.money})`)
  }
  savePlayer()
}

const giveItem = (item = null, from = "{r}Hacking{/}", status = null, verbose = true) => {
  IRC.require('r7rpg.items').getItem(item, from, status, verbose)
}

const saveItem = (item) => {
  player.items.push(item)
  savePlayer()
}

const removeItem = (item, verbose = false) => {
  if (player.items.includes(item)) {
    player.items.splice(player.items.indexOf(item), 1)
    savePlayer()
    if (verbose) {
      print(`{dc}${from}{/} discarded ${item}`)
    }
  }
}

const reset = () => {
  player.enemy = null
  player.status.type = 'idle'
  savePlayer()
}

const status = (status = null) => {
  if (status) {
    player.status.type = status
    savePlayer()
    return status
  } else {
    return player.status.type
  }
}

const damageEnemy = (amount) => {
  if (player.enemy) {
    if (player.enemy.name === 'Monqui') {
      const monquiRoll = IRC.require('r7rpg.game').roll7()
      if (monquiRoll.percent > 70) {
        player.enemy.health -= 50
      	print(`{r}Enemy {g}Monqui {r}has been nerfed by 50hp for {w}white supremacy`)
      } else if (monquiRoll.percent < 30) {
        player.enemy.health += 30
      	print(`Enemy {g}Monqui{/} ate some {y}beans{/}`)
      }
    }
    player.enemy.health -= amount
    if (player.enemy.health <= 0) {
      addXp(player.enemy.xp)
      credit(player.enemy.money)

      print(`{dc}${player_name}{/} killed the enemy {g}${player.enemy.name}{/} ({p}+${player.enemy.xp}xp {y}+$${player.enemy.money}{/})`)


	  if (player.items.includes("Lucky Dog's Dick") && Math.floor(Math.random() * 2) === 1) {
        giveItem()
        if (incrementItemInfo('luckyDogDickUses') > 20) {
          removeItem('Lucky Dog\'s Dick')
          setItemInfo('luckyDogDickUses', 0)
          print(`{dc}${from}{/} has worn his {y}Lucky Dog's Dick{/} right out`)
        }
      } else if (player.enemy.name === 'Giant Octopus') {
        giveItem()
      } else if (Math.floor(Math.random() * 30) === 7) {
        giveItem()
      }

      if (player.items.includes("Rusty Spanner")) {
        if (player.stats.current_health < player.stats.max_health) {
          healPlayer()
          if (incrementItemInfo('rustySpannerUses') > 100) {
            removeItem('Rusty Spanner')
            setItemInfo('rustySpannerUses', 0)
            print(`{dc}${from}'s {y}Rusty Spanner{/} has worn out`)
          }
        }
      }

      IRC.require('r7rpg.items').onBattleWon()
      IRC.require('r7rpg.items').onBattleEnd()

      reset()
    } else {
      enemyAttack()
    }
  } else {
    print(`{dc}${player_name}{/} couldn't find an enemy??`)
  }
  savePlayer()
}

const defChance = (intelligence1, intelligence2) => {
  const roll = IRC.require('r7rpg.game').roll7()
  if (intelligence1 > intelligence2) {
    if (roll.percent > 30) {
      return true
    } else {
      return false
    }
  } else {
    if (roll.percent > 60) {
      return true
    } else {
      return false
    }
  }
}

const enemyAttack = (defendable = true) => {
  const roll = IRC.require('r7rpg.game').roll7()
  let attack = Math.ceil(player.enemy.strength - (player.enemy.strength/100) * roll.percent)
  if (defendable && defChance(player.stats.intelligence, player.enemy.intelligence)) {
    const defenseRoll = IRC.require('r7rpg.game').roll7()
    const defense = Math.ceil(player.stats.defense - (player.stats.defense/100) * defenseRoll.percent)
    attack -= defense
    if (attack < 0) {
      attack = 0
    }
    print(`${roll.render} {y}*${roll.total}*{/} {dc}${player.enemy.name}{/} did {b}${attack}{/} ({g}-${defense} def{/}) damage`)
  } else {
    print(`${roll.render} {y}*${roll.total}*{/} {dc}${player.enemy.name}{/} did {b}${attack}{/} damage`)
  }
  damage(attack)
}

const fart = () => {
  print(`{dc}${player_name}{/} fart`)
  incrementInfo('farts')
  IRC.require('r7rpg.game').incrementInfo("farts")
  if (player.items.includes("Monqui's Beard")) {
    healPlayer(30)
  }
  if (player.enemy && player.enemy.name === "Jew") {
    damageEnemy(7777)
  }
}

const setItemInfo = (statName, value) => {
  if (player.itemInfo != undefined && player.itemInfo[statName] != undefined) {
    player.itemInfo[statName] = value
  } else {
    if (player.itemInfo == undefined) {
      player.itemInfo = {}
      player.itemInfo[statName] = value
    } else {
      player.itemInfo[statName] = value
    }
  }
}

const getItemInfo = (statName) => {
  if (player.itemInfo != undefined && player.itemInfo[statName] != undefined) {
    return player.itemInfo[statName]
  } else {
    return 0
  }
  savePlayer()
}

const  incrementItemInfo = (statName, value = 1) => {
  if (player.itemInfo != undefined && player.itemInfo[statName] != undefined) {
    player.itemInfo[statName] += value
  } else {
    if (player.itemInfo == undefined) {
      player.itemInfo = {}
      player.itemInfo[statName] = value
    } else {
      player.itemInfo[statName] = value
    }
  }
  savePlayer()
  return player.itemInfo[statName]
}

const evade = () => {
  if (player.status.type === 'combat') {
    const roll = IRC.require('r7rpg.game').roll7()
    let evadeChance = 25

    if (player.stats.agility > player.enemy.agility) {
      evadeChance = 50
    }

    if (player.items.includes("Running Shoes")) {
      player.status.type = 'idle'
      print(`{dc}${from}{/} escaped the {g}${player.enemy.name} {/}using{y} Running Shoes{/}`)
      player.enemy = null
      if (incrementItemInfo('runningShoesUses') > 10) {
        removeItem('Running Shoes')
        setItemInfo('runningShoesUses', 0)
        print(`{dc}${from}'s{y} Running Shoes{/} have worn out`)
      }
      incrementInfo('escapes')
      IRC.require('r7rpg.game').incrementInfo("escapes")
      savePlayer()
    } else if (roll.percent < evadeChance) {
      player.status.type = 'idle'
      print(`{b}${from} escaped the ${player.enemy.name}{/}`)
      player.enemy = null
      incrementInfo('escapes')
      IRC.require('r7rpg.game').incrementInfo("escapes")
      savePlayer()
    } else {
      print(`{r}${from} failed to escape ${player.enemy.name}{/}`)
      enemyAttack()
      savePlayer()
    }
  } else {
    throw new Error('You cannot escape nothing')
  }
}

const setInfo = (key, value) => {
  if (player.info != undefined && player.info[key] != undefined) {
    player.info[key] = value
  } else {
    if (player.info == undefined) {
      player.info = {}
      player.info[key] = value
    } else {
      player.info[key] = value
    }
  }
  savePlayer()
  return player.info[key]
}

const getInfo = (key) => {
  if (player.info != undefined && player.info[key] != undefined) {
    return player.info[key]
  } else {
    return 0
  }
  savePlayer()
}

const incrementInfo = (key, value = 1) => {
  if (player.info != undefined && player.info[key] != undefined) {
    player.info[key] += value
  } else {
    if (player.info == undefined) {
      player.info = {}
      player.info[key] = value
    } else {
      player.info[key] = value
    }
  }
  savePlayer()
  return player.info[key]
}

const bestLowest = (key, value, verbose = true) => {
  if (player.info != undefined && player.info[key] != undefined) {
    if (player.info[key] > value) {
      player.info[key] = value
      if (verbose) {
        print(`{dc}${from}{/} got a new {b}${key}{/} record of {y}${value}`)
      }
    }
  } else {
    if (player.info == undefined) {
      player.info = {}
      player.info[key] = value
    } else {
      player.info[key] = value
    }
  }
  savePlayer()
}

const bestHighest = (key, value, verbose = true) => {
  if (player.info != undefined && player.info[key] != undefined) {
    if (player.info[key] < value) {
      player.info[key] = value
      if (verbose) {
        print(`{dc}${from}{/} got a new {b}${key}{/} record of {y}${value}`)
      }
    }
  } else {
    if (player.info == undefined) {
      player.info = {}
      player.info[key] = value
    } else {
      player.info[key] = value
    }
  }
  savePlayer()
}

module.exports = {
  savePlayer,
  addXp,
  addHealth,
  healPlayer,
  addStrength,
  addDefense,
  addAgility,
  addIntelligence,
  levelUp,
  getAttack,
  damage,
  kill,
  charge,
  credit,
  giveItem,
  reset,
  status,
  damageEnemy,
  fart,
  removeItem,
  saveItem,
  revive,
  enemyAttack,
  evade,
  setItemInfo,
  getItemInfo,
  incrementItemInfo,
  setInfo,
  getInfo,
  incrementInfo,
  bestLowest,
  bestHighest
};
