command:
const dateFns = require('date-fns@1.30.1');if (!IRC.command.params[0] || !input.trim()) {
    throw `usage: {p}${IRC.colors.cmd('remind', 'message', 'time')}`
}
else {
    const now = new Date();
    const [time] = IRC.command.params;
    const timestamp = time ? IRC.parseTime(time) : now;
    const distance = dateFns.distanceInWordsStrict(timestamp, now);
    if (dateFns.isBefore(timestamp, now)) {
        throw new Error(`epoch fail - requested time is ${distance} ago`);
    }
    IRC.eventFns.addEvent('tick', {
        callback: 'remind.event',
        time: timestamp,
        message: input,
        target: IRC.message.isPM ? IRC.message.from : IRC.message.target,
    });
    print(`Reminder will display after {o}${distance}{/} on {dc}${dateFns.format(timestamp, 'dddd MMMM Do YYYY HH:mm:ss')}{/}`)
}
