const _ = require('lodash');
// link / text
function ribbit(subreddit) {
  	subreddit=subreddit.trim()
  	let obj = fetchSync.json(`https://www.reddit.com/r/${subreddit}/random/.json`)
    obj = Array.isArray(obj) ? obj[0] : obj;
    if (obj.error || !obj.data) {
      throw `no data found for ${subreddit}`;
    } else if (obj.data.children.length) {
      const post = _.shuffle(obj.data.children)[0].data;
      const permalink = `https://redd.it/${post.permalink.match(/\/comments\/([^/]+)/)[1]}/`
      print(`{dc}r/${post.subreddit}{/} - {bo}${post.title}{/} ${post.is_self? (
        `{b}{u}${permalink}`
      ) : (`- {b}{u}${post.url}{/}{/} - {b}{u}${permalink}`)}`)
	}
}

if (!module.required) {
 	ribbit(input); 
} else {
  	module.exports = ribbit;
}
