const roll = () => (0|Math.random()*6)+1;
const dice = Array.from({length: 7}, roll);
const total = dice.reduce((a, c) => a + c, 0);
const { from } = IRC.message;
IRC.require('module.count').next();
const best = store.get(from) || Infinity;

const diceRender = '{bl,gr} ' + dice.map(d => d == 1 ? '{bo}{p,gr}1{/}{bl,gr}' : d).join`-` + ' {/}';

if (total < best) {
    store.set(from, total);
    print(`${diceRender} {dc}${from}{/} got a new score of {y}${total}{/}!`);
}
else {
    print(`${diceRender} {dc}${from}{/} got {o}${total}{/}`);
}

if (total == 7) print('{y}~congratulations, you won roll7~')
