IRC.colors.leaderboard = () => {};

const useGame = () => IRC.require('module.loadObject')('game', {
    isRunning: false,
    scores: {},
    bets: {},
    history: [],
});

const [game, setGame] = useGame();

const numbers = [0,32,15,19,4,21,2,25,17,34,6,27,13,36,11,30,8,23,10,5,24,16,33,1,20,14,31,9,22,18,29,7,28,12,35,3,26];

const isRed = (num) => (
    ((num >= 1 && num <= 10 || num >= 19 && num <= 28) && num & 1)
    || (num >= 11 && num <= 18 || num >= 29 && num <= 36) && !(num & 1)
);

const isBlack = (num) => num !== 0 && !isRed(num);

const isValidLocation = (str) => /even|odd|\d+|red|black|low|high/.test(str);

const spin = () => 0|Math.random()*37;

const renderNumber = (num, withBall = false) => {
    const ball = withBall ? 'â— ' : '';
    if (!num) return `{w,dg} ${num} ${ball}{/}`;
    if (isRed(num)) return `{w,r} ${num} ${ball}{/}`;
    else return `{w,bl} ${num} ${ball}{/}`;
};

const [amount, ...locList] = input.trim().split(/\s+/);
const locations = locList.filter(isValidLocation);

if (!locations.length) {
    throw 'board locations are: number, red, black, even, odd, low, high - place bets with ' + IRC.colors.cmd('roulette', ['amount', 'location(s)'])
}

// min bet on red is 10
// min bet on num is 1

if (!game.isRunning) {
    game.isRunning = true;
    setGame(game);
    {
        sleep(10000);
        const [game, setGame] = useGame();
        const num = spin();
        // show board
        print(numbers.map(d => (
            renderNumber(d, d == num)
        )).join``);

        // show board
        // calculate scores...
        game.bets = {};
        game.isRunning = false;
        setGame(game);
    }
}
