// rust repl
getJSON('https://play.rust-lang.org/execute', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
        backtrace: false,
        channel: 'stable',
        code: getCode(input),
        crateType: 'bin',
        mode: 'debug',
        tests: false,
    }),
})
    .then(({success, stdout, stderr}) => {
        if (success) {
            print.raw(stdout);
            //stderr.trim() && print(`{r}${stderr}`);
        } else {
            print.raw(stderr.split('\n').filter(d => (
                (d.startsWith('error') &&
                !d.includes('error: aborting due to') &&
                d !== 'error: Could not compile `playground`.') ||
                d.includes('panicked')
            )).join`\n`);
        }
    })
    .catch(print.error);

function getCode(input) {
    const rx = /extern\s+crate\s+(.*?);/g;
    const crates = input.match(rx);
    if (crates) {
        const inner = input.replace(rx, '');
        return `${crates.join(' ')} fn main() { println!("{:?}", { ${inner} }) }`;
    }
    return `fn main() { println!("{:?}", { ${input} }) }`;
}
