command:
// Uses https://www.collinsdictionary.com/dictionary/english/ + input for defining the words

if(!IRC.wordList.includes(input.toUpperCase())){
  print(`${input} is not a valid Scrabble Word in CSW19!`)
  return
}

let data = fetchSync("https://www.collinsdictionary.com/dictionary/english/" + input,{type:'dom'})

definition = data.document.getElementsByClassName("sense")
origin = data.document.getElementsByClassName("etym")

if (definition.length > 0){
    print(definition[0].textContent)
}
else{
    getDOM(`https://en.m.wiktionary.org/wiki/` + input)
        .then(dom => {       
            dom.qsa('ol > li').splice(0, IRC.command.params[0] || 3).forEach(d => {
                const extra = d.querySelector('ul');
                extra && extra.remove();
                print(d.textContent)
            })    
        })
        .catch(print.error);
}

if (origin.length > 0){
    print(origin[0].textContent)
}
