const { from } = IRC.message;
const { wordList } = IRC;

function charScore(char){
    switch(char.toUpperCase()){
        case "A":
        case "E":
        case "I":
        case "O":
        case "N":
        case "R":
        case "T":
        case "L":
        case "S":
        case "U":
            return 1;
        case "D":
        case "G":
            return 2;
        case "B":
        case "C":
        case "M":
        case "P":
            return 3;
        case "F":
        case "H":
        case "V":
        case "W":
        case "Y":
            return 4;
        case "K":
            return 5;
        case "J":
        case "X":
            return 8;
        case "Q":
        case "Z":
            return 10;
        default:
            return 0;
    }
}

function round(num){
    return Math.round((num + 0.00001) * 100) / 100;
}

function calculateScore(word){
    let score = word.split("").reduce(((acc, curr) => charScore(curr) + acc),0) / 7;
    // go around the scaling issue
    return round(score)
}

function storeExist(key){
    return typeof(store.get(key)) !== "undefined"
}

function getGuesses(user){
    let key = user + "_guesses";
    return storeExist(key) ? +store.get(key) : 0
}

function setGuesses(user,reset=false){
    let key = user + "_guesses";
    return store.set(key,reset || !storeExist(key) ? 0 : +store.get(key) + 1)
}

function resetGuesses(user){
    getParticipants().map(user => setGuesses(user,true))
}

function getParticipants(){
    return storeExist("participants") ? JSON.parse(store.get("participants")) : []
}
function resetParticipants(){
    store.set("participants",undefined)
}

function addParticipant(user){
    participants = getParticipants()
    if (participants.indexOf(user) == -1){
        participants.push(user)
        store.set("participants",JSON.stringify(participants))
    }
}

function isValidAnagram(word1){
    return isValid(word1) && isAnagram(word1,store.get("game"));
}

function isValid(word){
    return wordList.indexOf(word) !== -1;
}

function isAnagram(word1,word2){
    return word1.split("").sort().join() == word2.split("").sort().join()
}

function isGameRunning(){
    return storeExist("game")
}

function saveHighScore(user){
    // Increase the High Score of a user who answered correctly
    let score = calculateScore(store.get("game"));
    if (storeExist(user)){
        store.set(user,+store.get(user) + score)
    }else{
        store.set(user,score)
    }
}

function startGame(){
    let word = getRandomWord();
    store.set("game",word);
    let shuffled = _.shuffle(word).join("")
    store.set("shuffled",shuffled);
    print(`Game started with word {dc}${shuffled}{/} worth ${calculateScore(shuffled)} points`)
    IRC.require('module.count').next()
}
function endGame(){
    resetGuesses();
    resetParticipants();
    store.set("game",undefined)
}

function getRandomWord(){
    let filtered = IRC.wordList.filter(x =>  x.length < 8);
    return filtered[Math.floor(Math.random() * filtered.length)];
}

function main(){
    if(isGameRunning()){
        if(!input){
            let shuffled = store.get("shuffled");
            print(`Game running with word {dc}${shuffled}{/} worth ${calculateScore(shuffled)} points`)
            print("Skip with scrabble.game.skip")
        }else{
            let user_try = getGuesses(from)
            if (user_try > 4){
                print(`Too many guesses for {dc}${from}{/}`)
            }
            else{
                setGuesses(from)
                addParticipant(from)
                if(isValidAnagram(input)){
                    saveHighScore(from);
                    print(`Correct from {dc}${from}{/}. {dc}${from}{/} has ${round(+store.get(from))} points`);
                    endGame();
                }else{
                    print(`Incorrect from {dc}${from}{/}, ${4 - user_try} guess${4 - user_try == 1 ? '' : 'es'} left`)
                }
            }
        }
    }else{
        if (input.length > 1){
            print("No game running dummy")
        }else{
         startGame();   
        }
    }
}

if(!module.required){
    main();
}

module.exports = {endGame,main,isGameRunning,round} ;
