const {isGameRunning } = IRC.require("scrabble.game")

String.prototype.replaceAll = function(search, replacement) {
    var target = this;
    return target.replace(new RegExp(search, 'g'), replacement);
};

async function getHintBackup(word){
    let hint_given = false;
    await getDOM(`https://en.m.wiktionary.org/wiki/` + word)
          .then(dom => {       
              dom.qsa('ol > li').splice(0, IRC.command.params[0] || 3).forEach(d => {
                  const extra = d.querySelector('ul');
                  extra && extra.remove();
                  print(censor(d.textContent, word))
                  hint_given = true;
              })    
          })
          .catch(print.error);
    return hint_given;
}

async function getHint(word){
    let data = fetchSync("https://www.collinsdictionary.com/dictionary/english/" + word,{type:'dom'})

    definition = data.document.getElementsByClassName("sense")
    origin = data.document.getElementsByClassName("etym")

    let hint_given = false;

    if (definition.length > 0){
        print(censor(definition[0].textContent,word))
        hint_given = true;
    }
    else{
        hint_given = await getHintBackup(word)
        if (!hint_given){
        print("No hint, must be alternative spelling")
      }
    }

    if(hint_given){
        store.set("hint","true")
    }
}

function censor(text,word){
    return text.replaceAll(word,"█".repeat(word.length)).replaceAll(word.substring(0,word.length-1),"█".repeat(word.length)).replaceAll(word.capitalize(),"█".repeat(word.length))
}

String.prototype.capitalize = function() {
  return this.charAt(0).toUpperCase() + this.slice(1)
}


if(isGameRunning()){
    getHint(store.get("game").toLowerCase())
}
else{
    print("No game running")
}
